  statusFile: './transcribe_status.json'
};

async function transcribeFile(page, filePath, fileName) {
  console.log(`\n[${new Date().toISOString()}] Processing: ${fileName}`);

  // 1. Go to homepage (logged out state for 3min trick)
  await page.goto('https://www.songscription.ai/');
  await page.waitForTimeout(2000);

  // 2. Upload file
  const fileInput = await page.$('input[type="file"]');
  await fileInput.setInputFiles(filePath);
  console.log('  File uploaded, waiting for settings dialog...');

  // 3. Wait for and complete settings
  await page.waitForSelector('text=What do you want to transcribe', { timeout: 30000 });
  await page.click('text=One instrument');
  await page.waitForTimeout(500);

  // Click Next Step
  await page.click('button:has-text("Next Step")');
  await page.waitForSelector('text=Select Instrument', { timeout: 10000 });

  // Select Piano
  await page.click('button:has-text("Piano")');
  await page.waitForTimeout(500);
  await page.click('button:has-text("Next Step")');

  // Copyright agreement
  await page.waitForSelector('text=Copyright Agreement', { timeout: 10000 });
  await page.click('button:has-text("I confirm")');
  await page.waitForTimeout(500);
  await page.click('button:has-text("Transcribe")');

  console.log('  Transcription started, waiting for completion...');

  // 4. Wait for transcription to complete
  await page.waitForURL(/\/transcribe\/[^/]+$/, { timeout: 300000 });
  const transcribeUrl = page.url();
  console.log(`  Completed: ${transcribeUrl}`);

  // 5. Login to unlock 3 minutes
  await page.click('text=Sign up');
  await page.waitForSelector('text=Sign in');
  await page.click('text=Sign in');
  await page.waitForTimeout(1000);

  await page.fill('input[type="email"], input[placeholder*="Email"]', CONFIG.email);
  await page.fill('input[type="password"]', CONFIG.password);
  await page.click('button:has-text("Sign In")');
  await page.waitForTimeout(3000);

  console.log('  Logged in, 3-minute access unlocked');

  // 6. Logout for next file
  await page.goto('https://www.songscription.ai/home');
  await page.waitForTimeout(1000);
  const userMenu = await page.$('button[aria-haspopup="dialog"]');
  if (userMenu) {
    await userMenu.click();
    await page.waitForTimeout(500);
    const signOut = await page.$('text=Sign Out');
    if (signOut) await signOut.click();
  }

  return transcribeUrl;
}

async function main() {
  const status = JSON.parse(fs.readFileSync(CONFIG.statusFile, 'utf8'));
  const pendingFiles = status.files.filter(f => f.status === 'pending');

  console.log(`Total files: ${status.files.length}, Pending: ${pendingFiles.length}`);

  const browser = await chromium.launch({ headless: false });
  const context = await browser.newContext();
  const page = await context.newPage();

  for (const fileInfo of pendingFiles) {
    const filePath = path.join(CONFIG.audioDir, fileInfo.file);

    try {
      fileInfo.status = 'processing';
      fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));

      const url = await transcribeFile(page, filePath, fileInfo.file);
      fileInfo.status = 'done';
      fileInfo.transcribeUrl = url;
      status.completedCount++;
    } catch (error) {
      console.error(`  Error: ${error.message}`);
      fileInfo.status = 'error';
      fileInfo.error = error.message;
    }

    fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));
  }

  await browser.close();
  console.log('\nAll done!');
}

main().catch(console.error);
