const { chromium } = require('playwright');
const fs = require('fs');
const path = require('path');

const CONFIG = {
  email: 'masterpianoscore@Gmail.com',
  password: 'Wenhao123',
  audioDir: '/Users/yay/Downloads/Benjamin_Bonum_Nocte',
  statusFile: './transcribe_status.json',
  errorFile: './error_log.json'
};

function logError(fileName, step, error, screenshot = null) {
  const errorLog = fs.existsSync(CONFIG.errorFile) 
    ? JSON.parse(fs.readFileSync(CONFIG.errorFile, 'utf8')) 
    : { errors: [], needsHelp: false };

  errorLog.errors.push({
    time: new Date().toISOString(),
    file: fileName,
    step: step,
    error: error.message || error,
    screenshot: screenshot
  });
  errorLog.needsHelp = true;
  errorLog.lastError = { file: fileName, step: step, time: new Date().toISOString() };

  fs.writeFileSync(CONFIG.errorFile, JSON.stringify(errorLog, null, 2));
  console.log(`[ERROR] ${step}: ${error.message || error}`);
  console.log('[PAUSED] Error logged. Waiting for intervention...');
}

async function transcribeFile(page, filePath, fileName, fileIndex) {
  const steps = [
    'navigate', 'upload', 'settings_dialog', 'one_instrument', 
    'next_step_1', 'select_piano', 'next_step_2', 'confirm_copyright',
    'click_transcribe', 'wait_completion', 'login', 'logout'
  ];
  let currentStep = '';

  try {
    // 1. Navigate
    currentStep = 'navigate';
    console.log(`\n[${fileIndex + 1}/28] ${fileName}`);
    console.log(`  Step: ${currentStep}`);
    await page.goto('https://www.songscription.ai/', { timeout: 30000 });
    await page.waitForTimeout(2000);

    // 2. Upload
    currentStep = 'upload';
    console.log(`  Step: ${currentStep}`);
    const fileInput = await page.$('input[type="file"]');
    if (!fileInput) throw new Error('File input not found');
    await fileInput.setInputFiles(filePath);

    // 3. Settings dialog
    currentStep = 'settings_dialog';
    console.log(`  Step: ${currentStep}`);
    await page.waitForSelector('text=What do you want to transcribe', { timeout: 60000 });

    // 4. One instrument
    currentStep = 'one_instrument';
    console.log(`  Step: ${currentStep}`);
    await page.click('text=One instrument', { timeout: 5000 });
    await page.waitForTimeout(800);

    // 5. Next step 1
    currentStep = 'next_step_1';
    console.log(`  Step: ${currentStep}`);
    const nextBtn1 = await page.$('button:has-text("Next Step"):not([disabled])');
    if (!nextBtn1) {
      await page.waitForTimeout(1000);
      await page.click('button:has-text("Next Step")');
    } else {
      await nextBtn1.click();
    }

    // 6. Select Piano
    currentStep = 'select_piano';
    console.log(`  Step: ${currentStep}`);
    await page.waitForSelector('text=Select Instrument', { timeout: 10000 });
    await page.click('button:has-text("Piano")', { timeout: 5000 });
    await page.waitForTimeout(800);

    // 7. Next step 2
    currentStep = 'next_step_2';
    console.log(`  Step: ${currentStep}`);
    await page.click('button:has-text("Next Step")');

    // 8. Confirm copyright
    currentStep = 'confirm_copyright';
    console.log(`  Step: ${currentStep}`);
    await page.waitForSelector('text=I confirm', { timeout: 10000 });
    await page.click('button:has-text("I confirm")');
    await page.waitForTimeout(800);

    // 9. Click transcribe
    currentStep = 'click_transcribe';
    console.log(`  Step: ${currentStep}`);
    await page.waitForSelector('button:has-text("Transcribe"):not([disabled])', { timeout: 5000 });
    await page.click('button:has-text("Transcribe")');

    // 10. Wait completion
    currentStep = 'wait_completion';
    console.log(`  Step: ${currentStep} (this may take 1-5 minutes)`);
    await page.waitForURL(/\/transcribe\/[a-f0-9-]+$/, { timeout: 300000 });
    const transcribeUrl = page.url();
    console.log(`  Completed: ${transcribeUrl}`);

    // 11. Login
    currentStep = 'login';
    console.log(`  Step: ${currentStep}`);
    const signUpBtn = await page.$('button:has-text("Sign up"), a:has-text("Sign up")');
    if (signUpBtn) {
      await signUpBtn.click();
      await page.waitForTimeout(1500);
      const signInLink = await page.$('text=Sign in');
      if (signInLink) await signInLink.click();
      await page.waitForTimeout(1500);

      await page.fill('input[placeholder*="mail"], input[type="email"]', CONFIG.email);
      await page.fill('input[type="password"]', CONFIG.password);
      await page.click('button:has-text("Sign In")');
      await page.waitForTimeout(3000);
      console.log(`  Logged in`);
    }

    // 12. Logout
    currentStep = 'logout';
    console.log(`  Step: ${currentStep}`);
    await page.goto('https://www.songscription.ai/home');
    await page.waitForTimeout(2000);
    const userMenu = await page.$('button[aria-haspopup="dialog"]');
    if (userMenu) {
      await userMenu.click();
      await page.waitForTimeout(800);
      const signOut = await page.$('button:has-text("Sign Out"), div:has-text("Sign Out")');
      if (signOut) {
        await signOut.click();
        await page.waitForTimeout(2000);
      }
    }
    console.log(`  Done!`);

    return { success: true, url: transcribeUrl };

  } catch (error) {
    const screenshotPath = `./error_${Date.now()}.png`;
    await page.screenshot({ path: screenshotPath });
    logError(fileName, currentStep, error, screenshotPath);
    return { success: false, step: currentStep, error: error.message };
  }
}

async function main() {
  // Clear previous error state
  if (fs.existsSync(CONFIG.errorFile)) {
    const errorLog = JSON.parse(fs.readFileSync(CONFIG.errorFile, 'utf8'));
    errorLog.needsHelp = false;
    fs.writeFileSync(CONFIG.errorFile, JSON.stringify(errorLog, null, 2));
  }

  const status = JSON.parse(fs.readFileSync(CONFIG.statusFile, 'utf8'));
  const pendingFiles = status.files.filter(f => f.status === 'pending');

  console.log(`=== Songscription Batch Transcriber ===`);
  console.log(`Total: ${status.files.length}, Completed: ${status.completedCount}, Pending: ${pendingFiles.length}`);

  const browser = await chromium.launch({ headless: false, slowMo: 100 });
  const context = await browser.newContext();
  const page = await context.newPage();

  for (let i = 0; i < status.files.length; i++) {
    const fileInfo = status.files[i];
    if (fileInfo.status !== 'pending') continue;

    const filePath = path.join(CONFIG.audioDir, fileInfo.file);

    if (!fs.existsSync(filePath)) {
      console.log(`[SKIP] File not found: ${fileInfo.file}`);
      fileInfo.status = 'error';
      fileInfo.error = 'File not found';
      continue;
    }

    fileInfo.status = 'processing';
    status.currentIndex = i;
    fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));

    const result = await transcribeFile(page, filePath, fileInfo.file, i);

    if (result.success) {
      fileInfo.status = 'done';
      fileInfo.transcribeUrl = result.url;
      status.completedCount++;
    } else {
      fileInfo.status = 'error';
      fileInfo.error = result.error;
      fileInfo.failedStep = result.step;

      // Wait for intervention
      console.log('\n[WAITING] Check error_log.json. Script will retry in 30 seconds...');
      await page.waitForTimeout(30000);

      // Retry once
      console.log('[RETRY] Attempting again...');
      fileInfo.status = 'pending';
      i--; // Retry same file
    }

    fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));
  }

  await browser.close();
  console.log('\n=== Batch Complete ===');
  console.log(`Completed: ${status.completedCount}/${status.files.length}`);
}

main().catch(console.error);
