const { chromium } = require('playwright');
const fs = require('fs');
const path = require('path');

const CONFIG = {
  email: 'masterpianoscore@Gmail.com',
  password: 'Wenhao123',
  audioDir: '/Users/yay/Downloads/Benjamin_Bonum_Nocte',
  statusFile: './transcribe_status.json'
};

async function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

async function clickButton(page, text, timeout = 10000) {
  try {
    await page.evaluate((btnText) => {
      const btn = Array.from(document.querySelectorAll('button')).find(b => 
        b.textContent.includes(btnText) && !b.disabled
      );
      if (btn) btn.click();
    }, text);
    await sleep(1000);
    return true;
  } catch (e) {
    console.log(`  Warning: Could not click '${text}'`);
    return false;
  }
}

async function transcribeFile(page, filePath, fileName, idx, total) {
  console.log(`\n[${idx}/${total}] ${fileName}`);

  let retries = 3;
  while (retries > 0) {
    try {
      await page.goto('https://www.songscription.ai/', { timeout: 60000 });
      await sleep(3000);

      // Upload file
      const fileInput = await page.$('input[type="file"]');
      if (!fileInput) throw new Error('File input not found');
      await fileInput.setInputFiles(filePath);
      console.log('  Uploaded, waiting for dialog...');

      // Wait for transcribe dialog
      await page.waitForSelector('text=What do you want to transcribe', { timeout: 60000 });
      await page.evaluate(() => document.querySelector('[role="radio"]')?.click());
      await sleep(1000);

      // First Next Step
      await clickButton(page, 'Next Step');
      await sleep(2000);

      // Select Instrument
      await page.waitForSelector('text=Select Instrument', { timeout: 20000 });
      await page.click('button:has-text("Piano")');
      await sleep(1000);

      // Second Next Step
      await clickButton(page, 'Next Step');
      await sleep(2000);

      // Copyright confirmation - KEY FIX: wait longer and try multiple methods
      console.log('  Waiting for copyright page...');
      await page.waitForSelector('text=I confirm', { timeout: 30000 });
      await sleep(2000);

      // Try to click I confirm button with multiple methods
      try {
        await page.click('button:has-text("I confirm")', { timeout: 10000 });
      } catch {
        await page.evaluate(() => {
          const btn = Array.from(document.querySelectorAll('button')).find(b => 
            b.textContent.includes('I confirm')
          );
          if (btn) {
            btn.click();
            btn.dispatchEvent(new Event('click', { bubbles: true }));
          }
        });
      }
      await sleep(2000);

      // Click Transcribe
      await clickButton(page, 'Transcribe');

      console.log('  Transcribing...');
      await page.waitForURL(/\/transcribe\/[a-f0-9-]+$/, { timeout: 300000 });
      const url = page.url();
      console.log('  Done:', url);

      // Login if needed
      const signUp = await page.$('button:has-text("Sign up")');
      if (signUp) {
        await signUp.click();
        await sleep(2000);
        await page.click('text=Sign in');
        await sleep(2000);
        await page.evaluate((cfg) => {
          const inputs = document.querySelectorAll('input');
          inputs[0].value = cfg.email;
          inputs[1].value = cfg.password;
          inputs[0].dispatchEvent(new Event('input', { bubbles: true }));
          inputs[1].dispatchEvent(new Event('input', { bubbles: true }));
        }, CONFIG);
        await page.click('button:has-text("Sign In")');
        await sleep(4000);
      }

      // Logout
      await page.goto('https://www.songscription.ai/home');
      await sleep(3000);
      await page.evaluate(() => {
        const menu = document.querySelector('button[aria-haspopup="dialog"]');
        if (menu) menu.click();
      });
      await sleep(1500);
      await page.evaluate(() => {
        const signOut = Array.from(document.querySelectorAll('button, div')).find(e => 
          e.textContent?.includes('Sign Out')
        );
        if (signOut) signOut.click();
      });
      await sleep(3000);

      return url;

    } catch (e) {
      console.error(`  Error (retries left: ${retries-1}):`, e.message);
      retries--;
      if (retries === 0) throw e;
      await sleep(5000);
      console.log('  Retrying...');
    }
  }
}

async function main() {
  const status = JSON.parse(fs.readFileSync(CONFIG.statusFile, 'utf8'));
  const pending = status.files.filter(f => f.status === 'pending' || f.status === 'error' || f.status === 'processing');
  const total = status.files.length;

  console.log(`=== Batch Transcriber v2 ===\nTotal: ${total}, Done: ${status.completedCount}, Pending/Error: ${pending.length}`);

  if (pending.length === 0) {
    console.log('No files to process!');
    return;
  }

  const browser = await chromium.launch({ headless: false });

  for (const f of pending) {
    const filePath = path.join(CONFIG.audioDir, f.file);
    if (!fs.existsSync(filePath)) { 
      f.status = 'error'; 
      f.error = 'File not found'; 
      fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));
      continue; 
    }

    // Create new page for each file to avoid browser crash issues
    const page = await browser.newPage();

    try {
      f.status = 'processing';
      f.error = null;
      fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));

      f.transcribeUrl = await transcribeFile(page, filePath, f.file, status.files.indexOf(f) + 1, total);
      f.status = 'done';
      status.completedCount++;
      console.log(`  ✓ Success! Total done: ${status.completedCount}/${total}`);
    } catch (e) {
      console.error('  ✗ Failed:', e.message);
      f.status = 'error';
      f.error = e.message;
    }

