const { chromium } = require('playwright');
const fs = require('fs');
const path = require('path');

(async () => {
  const browser = await chromium.launch({ headless: false });
  const context = await browser.newContext();
  const page = await context.newPage();

  // 拦截请求查看页面加载
  page.on('console', msg => console.log('PAGE LOG:', msg.text()));
  page.on('pageerror', err => console.log('PAGE ERROR:', err.message));

  await page.goto('https://www.songscription.ai/');
  await page.waitForTimeout(3000);

  // 使用 CDP 直接设置文件
  const filePath = '/Users/yay/Downloads/Benjamin_Bonum_Nocte/Benjamin Bonum Nocte - Baa Baa Mouton Noir Piano.mp3';

  // 找到文件输入框并设置文件
  const fileInput = await page.$('input[type="file"]');
  await fileInput.setInputFiles(filePath);

  console.log('File uploaded');

  // 等待转录对话框
  await page.waitForSelector('text=What do you want to transcribe', { timeout: 60000 });
  console.log('Step 1: Transcribe dialog appeared');

  // 点击单选按钮
  await page.evaluate(() => document.querySelector('[role="radio"]')?.click());
  await page.waitForTimeout(1000);

  // 点击 Next Step
  await page.evaluate(() => {
    const btn = Array.from(document.querySelectorAll('button')).find(b => b.textContent.includes('Next Step') && !b.disabled);
    btn?.click();
  });
  await page.waitForTimeout(2000);

  // 选择 Piano
  await page.click('button:has-text("Piano")');
  await page.waitForTimeout(1000);

  // 点击 Next Step
  await page.evaluate(() => {
    const btn = Array.from(document.querySelectorAll('button')).find(b => b.textContent.includes('Next Step') && !b.disabled);
    btn?.click();
  });

  console.log('Step 3: Waiting for copyright page...');
  await page.waitForTimeout(5000);

  // 获取当前页面所有文本
  const pageContent = await page.evaluate(() => {
    const texts = [];
    const elements = document.querySelectorAll('p, span, div, button, h1, h2, h3, h4, label');
    elements.forEach(el => {
      const text = el.textContent?.trim();
      if (text && text.length > 0 && text.length < 200) {
        texts.push(text);
      }
    });
    return [...new Set(texts)].slice(0, 50);
  });

  console.log('\n=== All visible text on page ===');
  pageContent.forEach((t, i) => console.log(`${i}: ${t}`));

  // 检查是否有复选框
  const checkboxes = await page.$$('input[type="checkbox"]');
  console.log(`\nFound ${checkboxes.length} checkboxes`);

  for (let i = 0; i < checkboxes.length; i++) {
    const info = await checkboxes[i].evaluate(el => ({
      id: el.id,
      name: el.name,
      checked: el.checked,
      parentText: el.parentElement?.textContent?.substring(0, 100)
    }));
    console.log(`Checkbox ${i}:`, info);
  }

  // 检查所有按钮状态
  const buttons = await page.evaluate(() => {
    return Array.from(document.querySelectorAll('button')).map((b, i) => ({
      index: i,
      text: b.textContent.trim(),
      disabled: b.disabled,
      visible: b.offsetParent !== null
    })).filter(b => b.visible);
  });

  console.log('\n=== All visible buttons ===');
  buttons.forEach(b => console.log(`${b.index}: "${b.text}" (disabled: ${b.disabled})`));

  await browser.close();
})();

const { chromium } = require('playwright');
const fs = require('fs');
const path = require('path');

const CONFIG = {
  email: 'masterpianoscore@Gmail.com',
  password: 'Wenhao123',
  audioDir: '/Users/yay/Downloads/Benjamin_Bonum_Nocte',
  statusFile: './transcribe_status.json'
};

async function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

async function clickButton(page, text, timeout = 10000) {
  try {
    await page.evaluate((btnText) => {
      const btn = Array.from(document.querySelectorAll('button')).find(b => 
        b.textContent.includes(btnText) && !b.disabled
      );
      if (btn) btn.click();
    }, text);
    await sleep(1000);
    return true;
  } catch (e) {
    console.log(`  Warning: Could not click '${text}'`);
    return false;
  }
}

async function transcribeFile(page, filePath, fileName, idx, total) {
  console.log(`\n[${idx}/${total}] ${fileName}`);

  let retries = 3;
  while (retries > 0) {
    try {
      await page.goto('https://www.songscription.ai/', { timeout: 60000 });
      await sleep(3000);

      // Upload file
      const fileInput = await page.$('input[type="file"]');
      if (!fileInput) throw new Error('File input not found');
      await fileInput.setInputFiles(filePath);
      console.log('  Uploaded, waiting for dialog...');

      // Wait for transcribe dialog
      await page.waitForSelector('text=What do you want to transcribe', { timeout: 60000 });
      await page.evaluate(() => document.querySelector('[role="radio"]')?.click());
      await sleep(1000);

      // First Next Step
      await clickButton(page, 'Next Step');
      await sleep(2000);

      // Select Instrument
      await page.waitForSelector('text=Select Instrument', { timeout: 20000 });
      await page.click('button:has-text("Piano")');
      await sleep(1000);

      // Second Next Step
      await clickButton(page, 'Next Step');
      await sleep(3000);

      // Copyright page - look for checkbox or enabled transcribe button
      console.log('  On copyright/confirmation page...');

      // Try to find and check any checkbox
      const hasCheckbox = await page.evaluate(() => {
        const cb = document.querySelector('input[type="checkbox"]');
        if (cb && !cb.checked) {
          cb.click();
          cb.checked = true;
          cb.dispatchEvent(new Event('change', { bubbles: true }));
          return true;
        }
        return !!cb;
      });

      if (hasCheckbox) {
        console.log('  Checked confirmation checkbox');
        await sleep(1000);
      }

      // Try to click Transcribe button (might need to wait for it to enable)
      let transcribeClicked = false;
      for (let attempt = 0; attempt < 5; attempt++) {
        transcribeClicked = await page.evaluate(() => {
          const btn = Array.from(document.querySelectorAll('button')).find(b => 
            b.textContent.includes('Transcribe') && !b.disabled
          );
          if (btn) {
            btn.click();
            return true;
          }
          return false;
        });

        if (transcribeClicked) {
          console.log('  Clicked Transcribe button');
          break;
        }

        await sleep(1000);
      }

      if (!transcribeClicked) {
        throw new Error('Could not find or click Transcribe button');
      }

      console.log('  Transcribing...');
      await page.waitForURL(/\/transcribe\/[a-f0-9-]+$/, { timeout: 300000 });
      const url = page.url();
      console.log('  Done:', url);

      // Login if needed
      const signUp = await page.$('button:has-text("Sign up")');
      if (signUp) {
        await signUp.click();
        await sleep(2000);
        await page.click('text=Sign in');
        await sleep(2000);
        await page.evaluate((cfg) => {
          const inputs = document.querySelectorAll('input');
          inputs[0].value = cfg.email;
          inputs[1].value = cfg.password;
          inputs[0].dispatchEvent(new Event('input', { bubbles: true }));
          inputs[1].dispatchEvent(new Event('input', { bubbles: true }));
        }, CONFIG);
        await page.click('button:has-text("Sign In")');
        await sleep(4000);
      }

      // Logout
      await page.goto('https://www.songscription.ai/home');
      await sleep(3000);
      await page.evaluate(() => {
        const menu = document.querySelector('button[aria-haspopup="dialog"]');
        if (menu) menu.click();
      });
      await sleep(1500);
      await page.evaluate(() => {
        const signOut = Array.from(document.querySelectorAll('button, div')).find(e => 
          e.textContent?.includes('Sign Out')
        );
        if (signOut) signOut.click();
      });
      await sleep(3000);

      return url;

    } catch (e) {
      console.error(`  Error (retries left: ${retries-1}):`, e.message);
      retries--;
      if (retries === 0) throw e;
      await sleep(5000);
      console.log('  Retrying...');
    }
  }
}

async function main() {
  const status = JSON.parse(fs.readFileSync(CONFIG.statusFile, 'utf8'));
  const pending = status.files.filter(f => f.status === 'pending' || f.status === 'error' || f.status === 'processing');
  const total = status.files.length;

  console.log(`=== Batch Transcriber v3 ===\nTotal: ${total}, Done: ${status.completedCount}, Pending/Error: ${pending.length}`);

  if (pending.length === 0) {
    console.log('No files to process!');
    return;
  }

  const browser = await chromium.launch({ headless: false });

  for (const f of pending) {
    const filePath = path.join(CONFIG.audioDir, f.file);
    if (!fs.existsSync(filePath)) { 
      f.status = 'error'; 
      f.error = 'File not found'; 
      fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));
      continue; 
    }

    // Create new page for each file to avoid browser crash issues
    const page = await browser.newPage();

    try {
      f.status = 'processing';
      f.error = null;
      fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));

      f.transcribeUrl = await transcribeFile(page, filePath, f.file, status.files.indexOf(f) + 1, total);
      f.status = 'done';
      status.completedCount++;
      console.log(`  ✓ Success! Total done: ${status.completedCount}/${total}`);
    } catch (e) {
      console.error('  ✗ Failed:', e.message);
      f.status = 'error';
      f.error = e.message;
    }

    fs.writeFileSync(CONFIG.statusFile, JSON.stringify(status, null, 2));

    // Close page after each file to prevent memory issues
    await page.close();
    await sleep(2000);
  }

  await browser.close();
  console.log('\n=== Done ===' );
}

main().catch(console.error);

