// Retry Manager - 自动重试管理模块

class RetryManager {
  constructor(logger, errorClassifier) {
    this.logger = logger;
    this.errorClassifier = errorClassifier;
    // 记录每个工具调用的重试次数: callId -> { attempts, lastError }
    this.retryState = new Map();
  }

  // 获取调用的重试状态
  getRetryState(callId) {
    return this.retryState.get(callId) || { attempts: 0, lastError: null };
  }

  // 记录一次重试
  recordAttempt(callId, error) {
    const state = this.getRetryState(callId);
    state.attempts++;
    state.lastError = error;
    state.lastAttemptTime = Date.now();
    this.retryState.set(callId, state);

    // 5分钟后清理
    setTimeout(() => this.retryState.delete(callId), 5 * 60 * 1000);

    return state;
  }

  // 判断是否应该自动重试
  shouldRetry(callId, error) {
    const classified = this.errorClassifier.classify(error);
    const state = this.getRetryState(callId);

    // 不可恢复的错误不重试
    if (!classified.recoverable) {
      return { shouldRetry: false, reason: 'not_recoverable' };
    }

    // 没有重试策略不重试
    if (!classified.retryStrategy) {
      return { shouldRetry: false, reason: 'no_retry_strategy' };
    }

    const { maxRetries } = classified.retryStrategy;

    // 超过最大重试次数不重试
    if (state.attempts >= maxRetries) {
      return { shouldRetry: false, reason: 'max_retries_exceeded', attempts: state.attempts };
    }

    return {
      shouldRetry: true,
      delay: classified.retryStrategy.delay || 1000,
      action: classified.retryStrategy.action || null,
      attemptNumber: state.attempts + 1,
      maxRetries
    };
  }

  // 执行重试前的修复动作
  async executePreRetryAction(action, hub) {
    this.logger.info(`[RetryManager] 执行修复动作: ${action}`);

    switch (action) {
      case 'reload_tools':
        // 重新加载工具
        await hub.reload();
        return { success: true, message: '工具已重新加载' };

      case 'rebuild_context':
        // 对于 Playwright，这个需要在 MCP 侧处理
        // 这里只是标记需要重建
        return { success: true, message: '标记需要重建上下文' };

      case 'refresh_snapshot':
        // 提示需要刷新快照
        return { success: true, message: '建议重新 take_snapshot' };

      default:
        return { success: false, message: '未知动作: ' + action };
    }
  }

  // 创建带重试的工具调用包装器
  async executeWithRetry(callId, tool, params, executeFn, hub) {
    let lastError = null;
    let attempts = 0;
    const maxAttempts = 3; // 最多尝试3次（1次原始 + 2次重试）

    while (attempts < maxAttempts) {
      try {
        attempts++;
        this.logger.info(`[RetryManager] 执行 ${tool} (尝试 ${attempts}/${maxAttempts})`);

        const result = await executeFn();

        // 成功，清理状态
        this.retryState.delete(callId);
        return { success: true, result, attempts };

      } catch (error) {
        lastError = error;
        this.recordAttempt(callId, error.message);

        const retryDecision = this.shouldRetry(callId, error);

        if (!retryDecision.shouldRetry) {
          this.logger.warning(`[RetryManager] 不再重试 ${tool}: ${retryDecision.reason}`);
          break;
        }

        this.logger.info(`[RetryManager] 将在 ${retryDecision.delay}ms 后重试 ${tool}`);

        // 执行修复动作
        if (retryDecision.action) {
          await this.executePreRetryAction(retryDecision.action, hub);
        }

        // 等待后重试
        await new Promise(resolve => setTimeout(resolve, retryDecision.delay));
      }
    }

    // 所有重试都失败
    return { 
      success: false, 
      error: lastError, 
      attempts,
      classified: this.errorClassifier.wrapError(lastError, tool)
    };
  }

  // 清理过期的重试状态
  cleanup() {
    const now = Date.now();
    const expireTime = 5 * 60 * 1000; // 5分钟

    for (const [callId, state] of this.retryState) {
      if (now - state.lastAttemptTime > expireTime) {
        this.retryState.delete(callId);
      }
    }
  }
}

export default RetryManager;

// State Manager - 任务状态机管理模块

import { writeFileSync, readFileSync, existsSync, mkdirSync } from 'fs';
import path from 'path';

// 任务状态枚举
const TaskState = {
  PENDING: 'pending',
  RUNNING: 'running',
  SUCCESS: 'success',
  FAILED: 'failed',
  RETRYING: 'retrying',
  PAUSED: 'paused',
  NEED_USER: 'need_user'
};

class StateManager {
  constructor(logger, storagePath = null) {
    this.logger = logger;
    this.storagePath = storagePath;
    // 当前活跃任务: taskId -> TaskState
    this.tasks = new Map();
    // 任务变量存储: taskId -> { varName: value }
    this.variables = new Map();
  }

  // 创建新任务
  createTask(taskId, steps = [], options = {}) {
    const task = {
      id: taskId,
      state: TaskState.PENDING,
      steps,
      currentStep: 0,
      totalSteps: steps.length,
      results: [],
      checkpoints: [],
      options: {
        stopOnError: true,
        timeout: 120000,
        ...options
      },
      createdAt: Date.now(),
      updatedAt: Date.now(),
      error: null
    };

    this.tasks.set(taskId, task);
    this.variables.set(taskId, {});

    this.logger.info(`[StateManager] 创建任务: ${taskId}, ${steps.length} 步`);
    return task;
  }

  // 获取任务
  getTask(taskId) {
    return this.tasks.get(taskId);
  }

  // 更新任务状态
  updateState(taskId, newState, extra = {}) {
    const task = this.tasks.get(taskId);
    if (!task) return null;

    const oldState = task.state;
    task.state = newState;
    task.updatedAt = Date.now();
    Object.assign(task, extra);

    this.logger.info(`[StateManager] 任务 ${taskId}: ${oldState} -> ${newState}`);
    return task;
  }

  // 记录步骤结果
  recordStepResult(taskId, stepIndex, result) {
    const task = this.tasks.get(taskId);
    if (!task) return null;

    task.results[stepIndex] = {
      ...result,
      timestamp: Date.now()
    };
    task.currentStep = stepIndex + 1;
    task.updatedAt = Date.now();

    // 如果步骤有 saveAs，保存到变量
    const step = task.steps[stepIndex];
    if (step?.saveAs && result.success) {
      this.setVariable(taskId, step.saveAs, {
        success: result.success,
        result: result.result,
        tool: step.tool
      });
    }

    return task;
  }

  // 创建检查点
  createCheckpoint(taskId) {
    const task = this.tasks.get(taskId);
    if (!task) return null;

    const checkpoint = {
      step: task.currentStep,
      state: task.state,
      results: [...task.results],
      variables: { ...this.variables.get(taskId) },
      timestamp: Date.now()
    };

    task.checkpoints.push(checkpoint);
    this.logger.info(`[StateManager] 任务 ${taskId}: 创建检查点 @ step ${checkpoint.step}`);

    return checkpoint;
  }

  // 从检查点恢复
  restoreFromCheckpoint(taskId, checkpointIndex = -1) {
    const task = this.tasks.get(taskId);
    if (!task || task.checkpoints.length === 0) return null;

    const idx = checkpointIndex < 0 ? task.checkpoints.length + checkpointIndex : checkpointIndex;
    const checkpoint = task.checkpoints[idx];
    if (!checkpoint) return null;

    task.currentStep = checkpoint.step;
    task.state = TaskState.PAUSED;
    task.results = [...checkpoint.results];
    this.variables.set(taskId, { ...checkpoint.variables });
    task.updatedAt = Date.now();

    this.logger.info(`[StateManager] 任务 ${taskId}: 从检查点 ${idx} 恢复到 step ${checkpoint.step}`);
    return task;
  }

  // 设置变量
  setVariable(taskId, name, value) {
    const vars = this.variables.get(taskId);
    if (vars) {
      vars[name] = value;
    }
  }

  // 获取变量
  getVariable(taskId, name) {
    const vars = this.variables.get(taskId);
    return vars ? vars[name] : undefined;
  }

  // 获取所有变量
  getAllVariables(taskId) {
    return this.variables.get(taskId) || {};
  }

  // 模板替换: 将 {{var.field}} 替换为实际值
  resolveTemplate(taskId, template) {
    if (typeof template !== 'string') {
      if (typeof template === 'object' && template !== null) {
        const resolved = {};
        for (const [key, value] of Object.entries(template)) {
          resolved[key] = this.resolveTemplate(taskId, value);
        }
        return resolved;
      }
      return template;
    }

    const vars = this.variables.get(taskId) || {};

    return template.replace(/\{\{([^}]+)\}\}/g, (match, path) => {
      const parts = path.trim().split('.');
      let value = vars;

      for (const part of parts) {
        if (value && typeof value === 'object') {
          value = value[part];
        } else {
          return match; // 保持原样
        }
      }

      if (value === undefined) return match;
      if (typeof value === 'object') return JSON.stringify(value);
      return String(value);
    });
  }

  // 评估条件
  evaluateCondition(taskId, condition) {
    if (!condition) return true;

    const vars = this.variables.get(taskId) || {};

    // 简单字符串条件
    if (condition === 'success') {
      // 检查上一步是否成功
      const task = this.tasks.get(taskId);
      if (!task || task.results.length === 0) return false;
      const lastResult = task.results[task.results.length - 1];
      return lastResult?.success === true;
    }

    // 对象条件
    if (typeof condition === 'object') {
      const { var: varName, success, contains, regex } = condition;
      const varValue = vars[varName];

      if (!varValue) return false;

      if (success !== undefined) {
        return varValue.success === success;
      }

      if (contains) {
        const resultStr = typeof varValue.result === 'string' 
          ? varValue.result 
          : JSON.stringify(varValue.result);
        return resultStr.includes(contains);
      }

      if (regex) {
        const resultStr = typeof varValue.result === 'string' 
          ? varValue.result 
          : JSON.stringify(varValue.result);
        return new RegExp(regex).test(resultStr);
      }
    }

    return true;
  }

  // 完成任务
  completeTask(taskId, success, error = null) {
    const task = this.tasks.get(taskId);
    if (!task) return null;

    task.state = success ? TaskState.SUCCESS : TaskState.FAILED;
    task.completedAt = Date.now();
    task.error = error;
    task.updatedAt = Date.now();

    this.logger.info(`[StateManager] 任务 ${taskId}: 完成 (${task.state})`);

    // 保存到文件（可选）
    if (this.storagePath) {
      this.saveTask(taskId);
    }

    return task;
  }

  // 保存任务到文件
  saveTask(taskId) {
    if (!this.storagePath) return;

    const task = this.tasks.get(taskId);
    if (!task) return;

    try {
      if (!existsSync(this.storagePath)) {
        mkdirSync(this.storagePath, { recursive: true });
      }

      const filePath = path.join(this.storagePath, `task-${taskId}.json`);
      writeFileSync(filePath, JSON.stringify({
        task,
        variables: this.variables.get(taskId)
      }, null, 2));

      this.logger.info(`[StateManager] 任务 ${taskId} 已保存`);
    } catch (e) {
      this.logger.error(`[StateManager] 保存任务失败: ${e.message}`);
    }
  }

  // 从文件加载任务
  loadTask(taskId) {
    if (!this.storagePath) return null;

    const filePath = path.join(this.storagePath, `task-${taskId}.json`);
    if (!existsSync(filePath)) return null;

    try {
      const data = JSON.parse(readFileSync(filePath, 'utf-8'));
      this.tasks.set(taskId, data.task);
      this.variables.set(taskId, data.variables || {});

      this.logger.info(`[StateManager] 任务 ${taskId} 已加载`);
      return data.task;
    } catch (e) {
      this.logger.error(`[StateManager] 加载任务失败: ${e.message}`);
      return null;
    }
  }

  // 清理已完成的任务
  cleanup(maxAge = 30 * 60 * 1000) {
    const now = Date.now();
    let cleaned = 0;

    for (const [taskId, task] of this.tasks) {
      if (task.state === TaskState.SUCCESS || task.state === TaskState.FAILED) {
        if (now - task.updatedAt > maxAge) {
          this.tasks.delete(taskId);
          this.variables.delete(taskId);
          cleaned++;
        }
      }
    }

    if (cleaned > 0) {
      this.logger.info(`[StateManager] 清理了 ${cleaned} 个过期任务`);
    }
  }

  // 获取任务摘要
  getTaskSummary(taskId) {
    const task = this.tasks.get(taskId);
    if (!task) return null;

    return {
      id: task.id,
      state: task.state,
      progress: `${task.currentStep}/${task.totalSteps}`,
      successSteps: task.results.filter(r => r?.success).length,
      failedSteps: task.results.filter(r => r && !r.success).length,
      duration: task.completedAt 
        ? task.completedAt - task.createdAt 
        : Date.now() - task.createdAt,
      error: task.error
    };
  }
}

export { TaskState };
export default StateManager;

// Task Engine - 批量任务执行引擎

import StateManager, { TaskState } from './state-manager.js';

class TaskEngine {
  constructor(logger, hub, safety, errorClassifier) {
    this.logger = logger;
    this.hub = hub;
    this.safety = safety;
    this.errorClassifier = errorClassifier;
    this.stateManager = new StateManager(logger);
  }

  // 执行批量任务
  async executeBatch(batchId, steps, options = {}, onStepComplete = null) {
    const task = this.stateManager.createTask(batchId, steps, options);
    this.stateManager.updateState(batchId, TaskState.RUNNING);

    const results = [];
    let success = true;

    for (let i = 0; i < steps.length; i++) {
      const step = steps[i];

      // 检查条件
      if (step.when && !this.stateManager.evaluateCondition(batchId, step.when)) {
        this.logger.info(`[TaskEngine] 跳过步骤 ${i}: 条件不满足`);
        results.push({
          stepIndex: i,
          skipped: true,
          reason: 'condition_not_met'
        });
        continue;
      }

      // 解析模板
      const resolvedParams = this.stateManager.resolveTemplate(batchId, step.params);

      // 安全检查
      const safetyCheck = await this.safety.checkOperation(step.tool, resolvedParams);
      if (!safetyCheck.allowed) {
        const stepResult = {
          stepIndex: i,
          tool: step.tool,
          success: false,
          error: safetyCheck.reason
        };
        results.push(stepResult);
        this.stateManager.recordStepResult(batchId, i, stepResult);

        if (options.stopOnError !== false) {
          success = false;
          break;
        }
        continue;
      }

      // 执行工具调用
      try {
        this.logger.info(`[TaskEngine] 执行步骤 ${i}: ${step.tool}`);
        const result = await this.hub.call(step.tool, resolvedParams);

        let resultStr = result;
        if (result && result.content && Array.isArray(result.content)) {
          resultStr = result.content.map(c => c.text || c).join('\n');
        }

        const stepResult = {
          stepIndex: i,
          tool: step.tool,
          success: true,
          result: typeof resultStr === 'string' ? resultStr : JSON.stringify(resultStr)
        };

        results.push(stepResult);
        this.stateManager.recordStepResult(batchId, i, stepResult);

        // 回调
        if (onStepComplete) {
          onStepComplete(stepResult);
        }

      } catch (e) {
        const classified = this.errorClassifier.wrapError(e, step.tool);

        const stepResult = {
          stepIndex: i,
          tool: step.tool,
          success: false,
          error: e.message,
          errorType: classified.errorType,
          recoverable: classified.recoverable,
          suggestion: classified.suggestion
        };

        results.push(stepResult);
        this.stateManager.recordStepResult(batchId, i, stepResult);

        // 回调
        if (onStepComplete) {
          onStepComplete(stepResult);
        }

        if (options.stopOnError !== false) {
          success = false;
          break;
        }
      }
    }

    // 完成任务
    this.stateManager.completeTask(batchId, success);

    return {
      batchId,
      success,
      stepsCompleted: results.filter(r => r.success).length,
      stepsFailed: results.filter(r => !r.skipped && !r.success).length,
      stepsSkipped: results.filter(r => r.skipped).length,
      totalSteps: steps.length,
      results
    };
  }

  // 从断点继续执行
  async resumeTask(taskId, onStepComplete = null) {
    const task = this.stateManager.getTask(taskId);
    if (!task) {
      return { success: false, error: '任务不存在' };
    }

    if (task.state === TaskState.SUCCESS) {
      return { success: true, message: '任务已完成' };
    }

    // 从当前步骤继续
    const remainingSteps = task.steps.slice(task.currentStep);
    if (remainingSteps.length === 0) {
      return { success: true, message: '没有剩余步骤' };
    }

    this.logger.info(`[TaskEngine] 继续任务 ${taskId}: 从步骤 ${task.currentStep} 开始`);

    // 重用现有任务状态，继续执行
    this.stateManager.updateState(taskId, TaskState.RUNNING);

    const results = [...task.results];
    let success = true;

    for (let i = task.currentStep; i < task.steps.length; i++) {
      const step = task.steps[i];

      // 检查条件
      if (step.when && !this.stateManager.evaluateCondition(taskId, step.when)) {
        results.push({ stepIndex: i, skipped: true, reason: 'condition_not_met' });
        continue;
      }

      const resolvedParams = this.stateManager.resolveTemplate(taskId, step.params);

      try {
        const result = await this.hub.call(step.tool, resolvedParams);

        let resultStr = result;
        if (result && result.content && Array.isArray(result.content)) {
          resultStr = result.content.map(c => c.text || c).join('\n');
        }

        const stepResult = {
          stepIndex: i,
          tool: step.tool,
          success: true,
          result: typeof resultStr === 'string' ? resultStr : JSON.stringify(resultStr)
        };

        results[i] = stepResult;
        this.stateManager.recordStepResult(taskId, i, stepResult);

        if (onStepComplete) onStepComplete(stepResult);

      } catch (e) {
        const classified = this.errorClassifier.wrapError(e, step.tool);

        const stepResult = {
          stepIndex: i,
          tool: step.tool,
          success: false,
          error: e.message,
          errorType: classified.errorType
        };

        results[i] = stepResult;
        this.stateManager.recordStepResult(taskId, i, stepResult);

        if (onStepComplete) onStepComplete(stepResult);

        if (task.options.stopOnError !== false) {
          success = false;
          break;
        }
      }
    }

    this.stateManager.completeTask(taskId, success);

    return {
      taskId,
      success,
      stepsCompleted: results.filter(r => r?.success).length,
      totalSteps: task.steps.length,
      results
    };
  }

  // 获取任务状态
  getTaskStatus(taskId) {
    return this.stateManager.getTaskSummary(taskId);
  }

  // 创建检查点
  createCheckpoint(taskId) {
    return this.stateManager.createCheckpoint(taskId);
  }

  // 从检查点恢复
  restoreCheckpoint(taskId, checkpointIndex = -1) {
    return this.stateManager.restoreFromCheckpoint(taskId, checkpointIndex);
  }
}

export default TaskEngine;

