const fs = require('fs');
const path = require('path');

// Todo list file
const todoFile = path.join(__dirname, 'todos.json');

// Read todos from file
function readTodos() {
  try {
    const data = fs.readFileSync(todoFile, 'utf8');
    return JSON.parse(data);
  } catch (err) {
    return [];
  }
}

// Write todos to file
function writeTodos(todos) {
  fs.writeFileSync(todoFile, JSON.stringify(todos, null, 2));
}

// Add a new todo
function addTodo(title) {
  const todos = readTodos();
  const newTodo = {
    id: Date.now(),
    title,
    completed: false,
    createdAt: new Date().toISOString()
  };
  todos.push(newTodo);
  writeTodos(todos);
  return newTodo;
}

// List all todos
function listTodos() {
  return readTodos();
}

// Toggle todo completion
function toggleTodo(id) {
  const todos = readTodos();
  const todo = todos.find(t => t.id === id);
  if (todo) {
    todo.completed = !todo.completed;
    writeTodos(todos);
  }
  return todo;
}

// Delete a todo
function deleteTodo(id) {
  const todos = readTodos();
  const filteredTodos = todos.filter(t => t.id !== id);
  writeTodos(filteredTodos);
  return filteredTodos.length !== todos.length;
}

// Main program
if (process.argv.length < 3) {
  console.log('Usage: node app.js [add|list|complete|delete] [arguments...]');
  process.exit(1);
}

const command = process.argv[2];

switch (command) {
  case 'add':
    if (process.argv.length < 4) {
      console.log('Usage: node app.js add "Todo title"');
      process.exit(1);
    }
    const todo = addTodo(process.argv.slice(3).join(' '));
    console.log(`Added: ${todo.title}`);
    break;

  case 'list':
    const todos = listTodos();
    if (todos.length === 0) {
      console.log('No todos found.');
    } else {
      console.log('Todos:');
      todos.forEach(todo => {
        console.log(`[${todo.completed ? 'x' : ' '}] ${todo.title}`);
      });
    }
    break;

  case 'complete':
    if (process.argv.length < 4) {
      console.log('Usage: node app.js complete <id>');
      process.exit(1);
    }
    const id = parseInt(process.argv[3]);
    const toggledTodo = toggleTodo(id);
    if (toggledTodo) {
      console.log(`Toggled: ${toggledTodo.title}`);
    } else {
      console.log('Todo not found.');
    }
    break;

  case 'delete':
    if (process.argv.length < 4) {
      console.log('Usage: node app.js delete <id>');
      process.exit(1);
    }
    const deleteId = parseInt(process.argv[3]);
    const deleted = deleteTodo(deleteId);
    if (deleted) {
      console.log('Deleted successfully.');
    } else {
      console.log('Todo not found.');
    }
    break;

  default:
    console.log('Unknown command. Available commands: add, list, complete, delete');
}

