:root {
  --primary: #2c3e50;
  --primary-dark: #1a252f;
  --accent: #c9a227;
  --accent-hover: #b89223;
  --bg-light: #f8f9fa;
  --bg-white: #ffffff;
  --text-dark: #333;
  --text-muted: #666;
  --text-light: #999;
  --border: #e0e0e0;
  --shadow: 0 2px 8px rgba(0,0,0,0.1);
  --shadow-hover: 0 4px 16px rgba(0,0,0,0.15);
  --radius: 8px;
  --transition: all 0.2s ease;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  line-height: 1.6;
  color: var(--text-dark);
  background: var(--bg-light);
}

/* Header */
.header {
  background: var(--primary);
  color: white;
  position: sticky;
  top: 0;
  z-index: 100;
  box-shadow: var(--shadow);
}

.header .container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 1rem 2rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 2rem;
}

.logo {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.logo-img {
  height: 40px;
  width: auto;
}

.logo h1 {
  font-family: 'Playfair Display', serif;
  font-size: 1.5rem;
  font-weight: 600;
  color: white;
}

.search-box {
  flex: 1;
  max-width: 400px;
  position: relative;
}

#searchInput {
  width: 100%;
  padding: 0.75rem 1rem 0.75rem 2.5rem;
  border: none;
  border-radius: var(--radius);
  font-size: 0.95rem;
  background: rgba(255,255,255,0.1);
  color: white;
  transition: var(--transition);
}

#searchInput::placeholder {
  color: rgba(255,255,255,0.6);
}

#searchInput:focus {
  outline: none;
  background: rgba(255,255,255,0.2);
}

.search-icon {
  position: absolute;
  left: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
  color: rgba(255,255,255,0.6);
}

.stats {
  font-size: 0.9rem;
  color: rgba(255,255,255,0.8);
}

/* A-Z Navigation */
.az-nav {
  background: var(--primary-dark);
  padding: 0.5rem 0;
  border-bottom: 1px solid rgba(255,255,255,0.1);
}

.az-nav .container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: center;
  gap: 0.25rem;
  flex-wrap: wrap;
}

.az-letter {
  padding: 0.4rem 0.6rem;
  color: rgba(255,255,255,0.7);
  text-decoration: none;
  font-size: 0.85rem;
  font-weight: 500;
  border-radius: 4px;
  transition: var(--transition);
  cursor: pointer;
}

.az-letter:hover {
  background: rgba(255,255,255,0.1);
  color: white;
}

.az-letter.active {
  background: var(--accent);
  color: var(--primary-dark);
}

.az-letter.disabled {
  color: rgba(255,255,255,0.3);
  cursor: default;
}

/* Main Content */
.main {
  max-width: 1400px;
  margin: 0 auto;
  padding: 2rem;
}

/* Featured Section */
.featured-section {
  margin-bottom: 3rem;
}

.section-title {
  font-family: 'Playfair Display', serif;
  font-size: 1.75rem;
  color: var(--primary);
  margin-bottom: 1.5rem;
  padding-bottom: 0.5rem;
  border-bottom: 2px solid var(--accent);
  display: inline-block;
}

.featured-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
  gap: 1.5rem;
}

/* Composer Card */
.composer-card {
  background: var(--bg-white);
  border-radius: var(--radius);
  overflow: hidden;
  box-shadow: var(--shadow);
  transition: var(--transition);
  cursor: pointer;
  text-align: center;
}

.composer-card:hover {
  transform: translateY(-4px);
  box-shadow: var(--shadow-hover);
}

.composer-portrait {
  width: 100%;
  aspect-ratio: 1;
  object-fit: cover;
  background: var(--bg-light);
}

.composer-card-info {
  padding: 1rem;
}

.composer-card-name {
  font-family: 'Playfair Display', serif;
  font-size: 1rem;
  font-weight: 600;
  color: var(--primary);
  margin-bottom: 0.25rem;
}

.composer-card-years {
  font-size: 0.8rem;
  color: var(--text-light);
  margin-bottom: 0.5rem;
}

.composer-card-count {
  font-size: 0.85rem;
  color: var(--accent);
  font-weight: 500;
}

/* All Composers Grid */
.composer-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 1rem;
}

.letter-section {
  grid-column: 1 / -1;
  margin-top: 2rem;
  margin-bottom: 1rem;
}

.letter-section:first-child {
  margin-top: 0;
}

.letter-heading {
  font-family: 'Playfair Display', serif;
  font-size: 2rem;
  color: var(--primary);
  padding-bottom: 0.5rem;
  border-bottom: 2px solid var(--border);
}

/* Score List */
.score-list {
  background: var(--bg-white);
  border-radius: var(--radius);
  box-shadow: var(--shadow);
}

.breadcrumb {
  padding: 1.5rem;
  border-bottom: 1px solid var(--border);
}

.breadcrumb a {
  color: var(--accent);
  text-decoration: none;
  font-size: 0.9rem;
}

.breadcrumb a:hover {
  text-decoration: underline;
}

.composer-header {
  display: flex;
  align-items: center;
  gap: 1.5rem;
  margin-top: 1rem;
}

.composer-portrait-large {
  width: 100px;
  height: 100px;
  border-radius: 50%;
  object-fit: cover;
  box-shadow: var(--shadow);
}

.composer-info h2 {
  font-family: 'Playfair Display', serif;
  font-size: 1.75rem;
  color: var(--primary);
}

.composer-score-count {
  color: var(--text-muted);
  margin-top: 0.25rem;
}

/* Category Filter */
.category-filter {
  padding: 1rem 1.5rem;
  border-bottom: 1px solid var(--border);
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
}

.filter-btn {
  padding: 0.5rem 1rem;
  border: 1px solid var(--border);
  border-radius: 20px;
  background: var(--bg-white);
  color: var(--text-muted);
  font-size: 0.85rem;
  cursor: pointer;
  transition: var(--transition);
}

.filter-btn:hover {
  border-color: var(--accent);
  color: var(--accent);
}

.filter-btn.active {
  background: var(--accent);
  border-color: var(--accent);
  color: white;
}

/* Scores Grid */
.scores-grid {
  padding: 1.5rem;
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 1rem;
}

.score-card {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem;
  border: 1px solid var(--border);
  border-radius: var(--radius);
  cursor: pointer;
  transition: var(--transition);
}

.score-card:hover {
  border-color: var(--accent);
  background: var(--bg-light);
}

.score-icon {
  width: 48px;
  height: 48px;
  background: var(--bg-light);
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--primary);
  font-size: 1.5rem;
  flex-shrink: 0;
}

.score-info {
  flex: 1;
  min-width: 0;
}

.score-name {
  font-weight: 500;
  color: var(--text-dark);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.score-meta {
  font-size: 0.8rem;
  color: var(--text-light);
  margin-top: 0.25rem;
}

.score-badges {
  display: flex;
  gap: 0.25rem;
}

.badge {
  padding: 0.2rem 0.5rem;
  border-radius: 4px;
  font-size: 0.7rem;
  font-weight: 500;
}

.badge-pdf {
  background: #e74c3c;
  color: white;
}

.badge-midi {
  background: #3498db;
  color: white;
}

/* Modal */
.modal {
  position: fixed;
  inset: 0;
  background: rgba(0,0,0,0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 2rem;
}

.modal.hidden {
  display: none;
}

.modal-content {
  background: var(--bg-white);
  border-radius: var(--radius);
  max-width: 900px;
  width: 100%;
  max-height: 90vh;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.modal-header {
  padding: 1rem 1.5rem;
  border-bottom: 1px solid var(--border);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.modal-header h3 {
  font-family: 'Playfair Display', serif;
  font-size: 1.25rem;
  color: var(--primary);
}

.modal-actions {
  display: flex;
  gap: 0.5rem;
  align-items: center;
}

.btn {
  padding: 0.5rem 1rem;
  border-radius: var(--radius);
  font-size: 0.9rem;
  font-weight: 500;
  text-decoration: none;
  cursor: pointer;
  transition: var(--transition);
  border: none;
}

.btn-download {
  background: var(--accent);
  color: white;
}

.btn-download:hover {
  background: var(--accent-hover);
}

.btn-midi {
  background: #3498db;
  color: white;
}

.btn-close {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: var(--bg-light);
  color: var(--text-muted);
  font-size: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
}

.btn-close:hover {
  background: var(--border);
}

.modal-body {
  flex: 1;
  overflow: auto;
  padding: 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
}

#pdfCanvas {
  max-width: 100%;
  box-shadow: var(--shadow);
}

.pdf-controls {
  margin-top: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
}

.btn-page {
  padding: 0.5rem 1rem;
  background: var(--bg-light);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  cursor: pointer;
}

.btn-page:hover {
  background: var(--border);
}

.page-info {
  color: var(--text-muted);
  font-size: 0.9rem;
}

/* Footer */
.footer {
  background: var(--primary);
  color: rgba(255,255,255,0.8);
  text-align: center;
  padding: 2rem;
  margin-top: 3rem;
}

/* Loading */
.loading {
  text-align: center;
  padding: 3rem;
  color: var(--text-muted);
}

/* Hidden */
.hidden {
  display: none !important;
}

/* Responsive */
@media (max-width: 768px) {
  .header .container {
    flex-wrap: wrap;
    padding: 1rem;
  }

  .search-box {
    order: 3;
    max-width: 100%;
    width: 100%;
    margin-top: 0.5rem;
  }

  .az-nav .container {
    padding: 0 1rem;
  }

  .main {
    padding: 1rem;
  }

  .featured-grid {
    grid-template-columns: repeat(auto-fill, minmax(140px, 1fr));
    gap: 1rem;
  }

  .composer-header {
    flex-direction: column;
    text-align: center;
  }
}
