document.addEventListener('DOMContentLoaded', () => {
    // DOM Elements
    const azNav = document.getElementById('azNav');
    const featuredGrid = document.getElementById('featuredGrid');
    const composerGrid = document.getElementById('composerGrid');
    const scoreList = document.getElementById('scoreList');
    const composerSection = document.getElementById('composerList');
    const featuredSection = document.getElementById('featuredSection');
    const searchResults = document.getElementById('searchResults');
    const searchInput = document.getElementById('searchInput');
    const composerCount = document.getElementById('composerCount');
    const scoreCount = document.getElementById('scoreCount');

    let allComposers = {};
    let currentLetter = null;

    // Featured composers list (top 20)
    const FEATURED_COMPOSERS = [
        'bach', 'beethoven', 'chopin', 'mozart', 'schubert',
        'schumann', 'liszt', 'debussy', 'rachmaninoff', 'brahms',
        'haydn', 'mendelssohn', 'grieg', 'dvorak', 'ravel',
        'faure', 'tschaikowski', 'haendel', 'vivaldi', 'satie'
    ];

    // Composer metadata (years, period)
    const COMPOSER_META = {
        'bach': { years: '1685-1750', period: 'baroque' },
        'beethoven': { years: '1770-1827', period: 'classical' },
        'chopin': { years: '1810-1849', period: 'romantic' },
        'mozart': { years: '1756-1791', period: 'classical' },
        'schubert': { years: '1797-1828', period: 'romantic' },
        'schumann': { years: '1810-1856', period: 'romantic' },
        'liszt': { years: '1811-1886', period: 'romantic' },
        'debussy': { years: '1862-1918', period: 'modern' },
        'rachmaninoff': { years: '1873-1943', period: 'romantic' },
        'brahms': { years: '1833-1897', period: 'romantic' },
        'haydn': { years: '1732-1809', period: 'classical' },
        'mendelssohn': { years: '1809-1847', period: 'romantic' },
        'grieg': { years: '1843-1907', period: 'romantic' },
        'dvorak': { years: '1841-1904', period: 'romantic' },
        'ravel': { years: '1875-1937', period: 'modern' },
        'faure': { years: '1845-1924', period: 'romantic' },
        'tschaikowski': { years: '1840-1893', period: 'romantic' },
        'haendel': { years: '1685-1759', period: 'baroque' },
        'vivaldi': { years: '1678-1741', period: 'baroque' },
        'satie': { years: '1866-1925', period: 'modern' },
        'czerny': { years: '1791-1857', period: 'classical' },
        'clementi': { years: '1752-1832', period: 'classical' },
        'burgmueller': { years: '1806-1874', period: 'romantic' },
    };

    // Build A-Z Navigation
    function buildAZNav(composers) {
        const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
        const available = new Set();

        Object.keys(composers).forEach(slug => {
            const first = slug.charAt(0).toUpperCase();
            available.add(first);
        });

        azNav.innerHTML = letters.map(letter => {
            const hasComposers = available.has(letter);
            const cls = hasComposers ? 'az-letter' : 'az-letter disabled';
            return `<span class="${cls}" data-letter="${letter}">${letter}</span>`;
        }).join('');

        // Add "All" button
        azNav.innerHTML = `<span class="az-letter active" data-letter="all">全部</span>` + azNav.innerHTML;

        // Click handlers
        azNav.querySelectorAll('.az-letter:not(.disabled)').forEach(el => {
            el.addEventListener('click', () => {
                azNav.querySelectorAll('.az-letter').forEach(e => e.classList.remove('active'));
                el.classList.add('active');
                const letter = el.dataset.letter;
                currentLetter = letter === 'all' ? null : letter;
                renderComposers(composers, currentLetter);
            });
        });
    }

    // Render Featured Composers
    function renderFeatured(composers) {
        const featured = FEATURED_COMPOSERS
            .filter(slug => composers[slug])
            .map(slug => ({ slug, ...composers[slug] }));

        featuredGrid.innerHTML = featured.map(c => {
            const meta = COMPOSER_META[c.slug] || {};
            const portrait = `assets/images/composers/${c.slug}.jpg`;
            return `
                <div class="composer-card" data-slug="${c.slug}">
                    <img src="${portrait}" alt="${c.name}" class="composer-portrait" 
                         onerror="this.src='assets/images/placeholder.jpg'">
                    <div class="composer-card-info">
                        <div class="composer-card-name">${c.name}</div>
                        <div class="composer-card-years">${meta.years || ''}</div>
                        <div class="composer-card-count">${c.scores.length} 份乐谱</div>
                    </div>
                </div>
            `;
        }).join('');

        // Click handlers
        featuredGrid.querySelectorAll('.composer-card').forEach(card => {
            card.addEventListener('click', () => showComposerScores(card.dataset.slug));
        });
    }

    // Render All Composers (grouped by letter)
    function renderComposers(composers, filterLetter = null) {
        const sorted = Object.entries(composers)
            .map(([slug, data]) => ({ slug, ...data }))
            .sort((a, b) => a.name.localeCompare(b.name));

        // Group by first letter
        const grouped = {};
        sorted.forEach(c => {
            const letter = c.slug.charAt(0).toUpperCase();
            if (filterLetter && letter !== filterLetter) return;
            if (!grouped[letter]) grouped[letter] = [];
            grouped[letter].push(c);
        });

        let html = '';
        Object.keys(grouped).sort().forEach(letter => {
            html += `<div class="letter-section"><h3 class="letter-heading">${letter}</h3></div>`;
            grouped[letter].forEach(c => {
                const meta = COMPOSER_META[c.slug] || {};
                const portrait = `assets/images/composers/${c.slug}.jpg`;
                html += `
                    <div class="composer-card" data-slug="${c.slug}">
                        <img src="${portrait}" alt="${c.name}" class="composer-portrait"
                             onerror="this.src='assets/images/placeholder.jpg'">
                        <div class="composer-card-info">
                            <div class="composer-card-name">${c.name}</div>
                            <div class="composer-card-years">${meta.years || ''}</div>
                            <div class="composer-card-count">${c.scores.length} 份乐谱</div>
                        </div>
                    </div>
                `;
            });
        });

        composerGrid.innerHTML = html || '<div class="loading">没有找到作曲家</div>';

        // Click handlers
        composerGrid.querySelectorAll('.composer-card').forEach(card => {
            card.addEventListener('click', () => showComposerScores(card.dataset.slug));
        });
    }

    // Show Composer's Scores
    function showComposerScores(slug) {
        const composer = allComposers[slug];
        if (!composer) return;

        const meta = COMPOSER_META[slug] || {};
        document.getElementById('currentComposer').textContent = composer.name;
        document.getElementById('composerYears').textContent = meta.years || '';
        document.getElementById('composerScoreCount').textContent = `${composer.scores.length} 份乐谱`;
        document.getElementById('composerPortrait').src = `assets/images/composers/${slug}.jpg`;
        document.getElementById('composerPortrait').onerror = function() {
            this.src = 'assets/images/placeholder.jpg';
        };

        // Get categories
        const categories = [...new Set(composer.scores.map(s => s.category).filter(Boolean))];
        const categoryFilter = document.getElementById('categoryFilter');

        if (categories.length > 0) {
            categoryFilter.innerHTML = `
                <button class="filter-btn active" data-category="all">全部</button>
                ${categories.map(cat => `<button class="filter-btn" data-category="${cat}">${cat}</button>`).join('')}
            `;
            categoryFilter.querySelectorAll('.filter-btn').forEach(btn => {
                btn.addEventListener('click', () => {
                    categoryFilter.querySelectorAll('.filter-btn').forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                    renderScores(composer.scores, btn.dataset.category === 'all' ? null : btn.dataset.category);
                });
            });
        } else {
            categoryFilter.innerHTML = '';
        }

        renderScores(composer.scores);

        // Show/hide sections
        featuredSection.classList.add('hidden');
        composerSection.classList.add('hidden');
        searchResults.classList.add('hidden');
        scoreList.classList.remove('hidden');

        // Scroll to top
        window.scrollTo(0, 0);
    }

    // Render Scores
    function renderScores(scores, filterCategory = null) {
        const filtered = filterCategory 
            ? scores.filter(s => s.category === filterCategory)
            : scores;

        const scoresGrid = document.getElementById('scoresGrid');
        scoresGrid.innerHTML = filtered.map(score => `
            <div class="score-card" data-pdf="${score.pdf}" data-midi="${score.midi || ''}" data-name="${score.name}">
                <div class="score-icon">♪</div>
                <div class="score-info">
                    <div class="score-name">${score.name}</div>
                    <div class="score-meta">${score.category || '未分类'}</div>
                </div>
                <div class="score-badges">
                    <span class="badge badge-pdf">PDF</span>
                    ${score.midi ? '<span class="badge badge-midi">MIDI</span>' : ''}
                </div>
            </div>
        `).join('');

        // Click handlers
        scoresGrid.querySelectorAll('.score-card').forEach(card => {
            card.addEventListener('click', () => openPdfModal(card.dataset.pdf, card.dataset.midi, card.dataset.name));
        });
    }

    // Back to composers
    document.getElementById('backToComposers').addEventListener('click', (e) => {
        e.preventDefault();
        scoreList.classList.add('hidden');
        featuredSection.classList.remove('hidden');
        composerSection.classList.remove('hidden');
    });

    // Search
    let searchTimeout;
    searchInput.addEventListener('input', (e) => {
        clearTimeout(searchTimeout);
        searchTimeout = setTimeout(() => {
            const term = e.target.value.trim().toLowerCase();
            if (term.length < 2) {
                searchResults.classList.add('hidden');
                featuredSection.classList.remove('hidden');
                composerSection.classList.remove('hidden');
                return;
            }
            performSearch(term);
        }, 300);
    });

    function performSearch(term) {
        document.getElementById('searchTerm').textContent = term;

        // Search composers
        const matchedComposers = Object.entries(allComposers)
            .filter(([slug, data]) => 
                slug.includes(term) || 
                data.name.toLowerCase().includes(term)
            )
            .map(([slug, data]) => ({ slug, ...data }));

        // Search scores
        const matchedScores = [];
        Object.entries(allComposers).forEach(([slug, data]) => {
            data.scores.forEach(score => {
                if (score.name.toLowerCase().includes(term)) {
                    matchedScores.push({ ...score, composerSlug: slug, composerName: data.name });
                }
            });
        });

        let html = '';

        if (matchedComposers.length > 0) {
            html += '<div class="letter-section"><h3 class="letter-heading">作曲家</h3></div>';
            matchedComposers.forEach(c => {
                const meta = COMPOSER_META[c.slug] || {};
                html += `
                    <div class="composer-card" data-slug="${c.slug}">
                        <img src="assets/images/composers/${c.slug}.jpg" alt="${c.name}" class="composer-portrait"
                             onerror="this.src='assets/images/placeholder.jpg'">
                        <div class="composer-card-info">
                            <div class="composer-card-name">${c.name}</div>
                            <div class="composer-card-years">${meta.years || ''}</div>
                            <div class="composer-card-count">${c.scores.length} 份乐谱</div>
                        </div>
                    </div>
                `;
            });
        }

        if (matchedScores.length > 0) {
            html += '<div class="letter-section"><h3 class="letter-heading">乐谱</h3></div>';
            matchedScores.slice(0, 50).forEach(score => {
                html += `
                    <div class="score-card" data-pdf="${score.pdf}" data-midi="${score.midi || ''}" data-name="${score.name}">
                        <div class="score-icon">♪</div>
                        <div class="score-info">
                            <div class="score-name">${score.name}</div>
                            <div class="score-meta">${score.composerName}</div>
                        </div>
                        <div class="score-badges">
                            <span class="badge badge-pdf">PDF</span>
                            ${score.midi ? '<span class="badge badge-midi">MIDI</span>' : ''}
                        </div>
                    </div>
                `;
            });
        }

        if (!html) {
            html = '<div class="loading">没有找到相关结果</div>';
        }

        document.getElementById('searchResultsContent').innerHTML = html;

        // Add click handlers
        document.querySelectorAll('#searchResultsContent .composer-card').forEach(card => {
            card.addEventListener('click', () => showComposerScores(card.dataset.slug));
        });
        document.querySelectorAll('#searchResultsContent .score-card').forEach(card => {
            card.addEventListener('click', () => openPdfModal(card.dataset.pdf, card.dataset.midi, card.dataset.name));
        });

        featuredSection.classList.add('hidden');
        composerSection.classList.add('hidden');
        scoreList.classList.add('hidden');
        searchResults.classList.remove('hidden');
    }

    document.getElementById('clearSearch').addEventListener('click', (e) => {
        e.preventDefault();
        searchInput.value = '';
        searchResults.classList.add('hidden');
        featuredSection.classList.remove('hidden');
        composerSection.classList.remove('hidden');
    });

    // PDF Modal
    let pdfDoc = null;
    let currentPageNum = 1;

    function openPdfModal(pdfPath, midiPath, name) {
        document.getElementById('pdfTitle').textContent = name;
        document.getElementById('downloadPdf').href = '../' + pdfPath;

        const midiBtn = document.getElementById('downloadMidi');
        if (midiPath) {
            midiBtn.href = '../' + midiPath;
            midiBtn.classList.remove('hidden');
        } else {
            midiBtn.classList.add('hidden');
        }

        document.getElementById('pdfModal').classList.remove('hidden');
        document.getElementById('pdfLoading').style.display = 'block';
        document.getElementById('pdfCanvas').style.display = 'none';

        // Load PDF
        pdfjsLib.getDocument('../' + pdfPath).promise.then(pdf => {
            pdfDoc = pdf;
            document.getElementById('totalPages').textContent = pdf.numPages;
            currentPageNum = 1;
            renderPage(1);
        }).catch(err => {
            console.error('PDF load error:', err);
            document.getElementById('pdfLoading').textContent = '加载失败';
        });
    }

    function renderPage(num) {
        pdfDoc.getPage(num).then(page => {
            const canvas = document.getElementById('pdfCanvas');
            const ctx = canvas.getContext('2d');
            const viewport = page.getViewport({ scale: 1.5 });

            canvas.height = viewport.height;
            canvas.width = viewport.width;

            page.render({ canvasContext: ctx, viewport }).promise.then(() => {
                document.getElementById('pdfLoading').style.display = 'none';
                canvas.style.display = 'block';
                document.getElementById('currentPage').textContent = num;
            });
        });
    }

    document.getElementById('prevPage').addEventListener('click', () => {
        if (currentPageNum <= 1) return;
        currentPageNum--;
        renderPage(currentPageNum);
    });

    document.getElementById('nextPage').addEventListener('click', () => {
        if (currentPageNum >= pdfDoc.numPages) return;
        currentPageNum++;
        renderPage(currentPageNum);
    });

    document.getElementById('closeModal').addEventListener('click', () => {
        document.getElementById('pdfModal').classList.add('hidden');
    });

    document.getElementById('pdfModal').addEventListener('click', (e) => {
        if (e.target === e.currentTarget) {
            document.getElementById('pdfModal').classList.add('hidden');
        }
    });

    // Load data
    async function loadData() {
        try {
            const res = await fetch('data/index.json');
            allComposers = await res.json();

            // Update stats
            const totalScores = Object.values(allComposers).reduce((sum, c) => sum + c.scores.length, 0);
            composerCount.textContent = Object.keys(allComposers).length;
            scoreCount.textContent = totalScores;

            // Build UI
            buildAZNav(allComposers);
            renderFeatured(allComposers);
            renderComposers(allComposers);
        } catch (err) {
            console.error('Load error:', err);
            composerGrid.innerHTML = '<div class="loading">加载失败，请刷新重试</div>';
        }
    }

    loadData();
});
