        'romantic': { label: '浪漫', years: '1820-1900', color: '#c62828' },
        'modern': { label: '现代', years: '1900+', color: '#1565c0' }
    };

    // Build A-Z Navigation
    function buildAZNav(composers) {
        const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
        const available = new Set();

        Object.keys(composers).forEach(slug => {
            available.add(slug.charAt(0).toUpperCase());
        });

        let html = `<span class="az-letter active" data-letter="all">全部</span>`;
        html += letters.map(letter => {
            const hasComposers = available.has(letter);
            const cls = hasComposers ? 'az-letter' : 'az-letter disabled';
            return `<span class="${cls}" data-letter="${letter}">${letter}</span>`;
        }).join('');

        azNav.innerHTML = html;

        azNav.querySelectorAll('.az-letter:not(.disabled)').forEach(el => {
            el.addEventListener('click', () => {
                azNav.querySelectorAll('.az-letter').forEach(e => e.classList.remove('active'));
                el.classList.add('active');
                currentLetter = el.dataset.letter === 'all' ? null : el.dataset.letter;
                renderComposers(allComposers, currentLetter, currentPeriod);
            });
        });
    }

    // Build Period Filter
    function buildPeriodFilter() {
        const container = document.createElement('div');
        container.className = 'period-filter';
        container.id = 'periodFilter';

        let html = `<span class="period-btn active" data-period="all">全部时期</span>`;
        Object.entries(PERIODS).forEach(([key, val]) => {
            html += `<span class="period-btn" data-period="${key}" style="--period-color: ${val.color}">${val.label} <small>${val.years}</small></span>`;
        });

        container.innerHTML = html;

        // Insert after section title
        const sectionTitle = composerSection.querySelector('.section-title');
        sectionTitle.after(container);

        container.querySelectorAll('.period-btn').forEach(btn => {
            btn.addEventListener('click', () => {
                container.querySelectorAll('.period-btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                currentPeriod = btn.dataset.period === 'all' ? null : btn.dataset.period;
                renderComposers(allComposers, currentLetter, currentPeriod);
            });
        });
    }

    // Render Featured Composers
    function renderFeatured(composers) {
        const featured = FEATURED_COMPOSERS
            .filter(slug => composers[slug])
            .map(slug => ({ slug, ...composers[slug] }));

        featuredGrid.innerHTML = featured.map(c => {
            const meta = composerMeta[c.slug] || {};
            const period = PERIODS[meta.period];
