                             onerror="this.src='assets/images/composers/placeholder.jpg'">
                        <div class="composer-card-info">
                            <div class="composer-card-name">${c.name}</div>
                            <div class="composer-card-years">${c.meta.years || ''}</div>
                            ${period ? `<div class="composer-card-period" style="color: ${period.color}">${period.label}</div>` : ''}
                            <div class="composer-card-count">${c.scores.length} 份乐谱</div>
                        </div>
                    </div>
                `;
            });
        });

        composerGrid.innerHTML = html || '<div class="loading">没有找到作曲家</div>';

        composerGrid.querySelectorAll('.composer-card').forEach(card => {
            card.addEventListener('click', () => showComposerScores(card.dataset.slug));
        });
    }

    // Show Composer's Scores
    function showComposerScores(slug) {
        const composer = allComposers[slug];
        if (!composer) return;

        const meta = composerMeta[slug] || {};
        const period = PERIODS[meta.period];

        document.getElementById('currentComposer').textContent = composer.name;
        document.getElementById('composerYears').textContent = meta.years || '';
        document.getElementById('composerScoreCount').textContent = `${composer.scores.length} 份乐谱`;

        if (period) {
            document.getElementById('composerYears').innerHTML = 
                `${meta.years || ''} <span style="color: ${period.color}; margin-left: 0.5rem;">${period.label}</span>`;
        }

        const portrait = document.getElementById('composerPortrait');
        portrait.src = `assets/images/composers/${slug}.jpg`;
        portrait.onerror = function() { this.src = 'assets/images/composers/placeholder.jpg'; };

        // Categories
        const categories = [...new Set(composer.scores.map(s => s.category).filter(Boolean))];
        const categoryFilter = document.getElementById('categoryFilter');

        if (categories.length > 0) {
            categoryFilter.innerHTML = `
                <button class="filter-btn active" data-category="all">全部</button>
                ${categories.map(cat => `<button class="filter-btn" data-category="${cat}">${cat}</button>`).join('')}
            `;
            categoryFilter.querySelectorAll('.filter-btn').forEach(btn => {
                btn.addEventListener('click', () => {
                    categoryFilter.querySelectorAll('.filter-btn').forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                    renderScores(composer.scores, btn.dataset.category === 'all' ? null : btn.dataset.category);
                });
            });
        } else {
            categoryFilter.innerHTML = '';
        }

        renderScores(composer.scores);

        featuredSection.classList.add('hidden');
