    function performSearch(term) {
        document.getElementById('searchTerm').textContent = term;

        const matchedComposers = Object.entries(allComposers)
            .filter(([slug, data]) => slug.includes(term) || data.name.toLowerCase().includes(term))
            .map(([slug, data]) => ({ slug, ...data, meta: composerMeta[slug] || {} }));

        const matchedScores = [];
        Object.entries(allComposers).forEach(([slug, data]) => {
