// Shopping Cart Module
const Cart = {
    items: [],
    defaultPrice: 2.99,

    init() {
        // Load from localStorage
        const saved = localStorage.getItem('ezmusicstore_cart');
        if (saved) {
            this.items = JSON.parse(saved);
        }
        this.bindEvents();
        this.updateUI();
    },

    bindEvents() {
        // Cart button
        document.getElementById('cartBtn').addEventListener('click', () => this.toggleSidebar());
        document.getElementById('cartClose').addEventListener('click', () => this.closeSidebar());
        document.getElementById('cartOverlay').addEventListener('click', () => this.closeSidebar());

        // Checkout
        document.getElementById('checkoutBtn').addEventListener('click', () => this.openCheckout());
        document.getElementById('closeCheckout').addEventListener('click', () => this.closeCheckout());
        document.getElementById('checkoutForm').addEventListener('submit', (e) => this.handleCheckout(e));

        // Success modal
        document.getElementById('closeSuccess').addEventListener('click', () => {
            document.getElementById('successModal').classList.add('hidden');
        });
    },

    add(item) {
        // Check if already in cart
        const exists = this.items.find(i => i.pdf === item.pdf);
        if (exists) {
            this.showNotification('该乐谱已在购物车中');
            return;
        }

        this.items.push({
            ...item,
            price: item.price || this.defaultPrice,
            addedAt: Date.now()
        });

        this.save();
        this.updateUI();
        this.showNotification('已添加到购物车');
        this.openSidebar();
    },

    remove(pdf) {
        this.items = this.items.filter(i => i.pdf !== pdf);
        this.save();
        this.updateUI();
    },

    clear() {
        this.items = [];
        this.save();
        this.updateUI();
    },

    save() {
        localStorage.setItem('ezmusicstore_cart', JSON.stringify(this.items));
    },

    getTotal() {
        return this.items.reduce((sum, item) => sum + item.price, 0);
    },

    updateUI() {
        // Update cart count
        const count = this.items.length;
        document.getElementById('cartCount').textContent = count;
        document.getElementById('cartCount').style.display = count > 0 ? 'flex' : 'none';

        // Update cart items
        const cartItems = document.getElementById('cartItems');
        if (count === 0) {
            cartItems.innerHTML = '<div class="cart-empty">购物车是空的</div>';
        } else {
            cartItems.innerHTML = this.items.map(item => `
                <div class="cart-item" data-pdf="${item.pdf}">
                    <div class="cart-item-info">
                        <div class="cart-item-name">${item.name}</div>
                        <div class="cart-item-composer">${item.composer || ''}</div>
                    </div>
                    <div class="cart-item-price">$${item.price.toFixed(2)}</div>
                    <button class="cart-item-remove" data-pdf="${item.pdf}">×</button>
                </div>
            `).join('');

            // Bind remove buttons
            cartItems.querySelectorAll('.cart-item-remove').forEach(btn => {
                btn.addEventListener('click', () => this.remove(btn.dataset.pdf));
            });
        }

        // Update total
        document.getElementById('cartTotal').textContent = '$' + this.getTotal().toFixed(2);

        // Update checkout button
        document.getElementById('checkoutBtn').disabled = count === 0;
    },

    toggleSidebar() {
        const sidebar = document.getElementById('cartSidebar');
        const overlay = document.getElementById('cartOverlay');
        sidebar.classList.toggle('hidden');
        overlay.classList.toggle('hidden');
    },

    openSidebar() {
        document.getElementById('cartSidebar').classList.remove('hidden');
        document.getElementById('cartOverlay').classList.remove('hidden');
    },

    closeSidebar() {
        document.getElementById('cartSidebar').classList.add('hidden');
        document.getElementById('cartOverlay').classList.add('hidden');
    },

    openCheckout() {
        this.closeSidebar();

        // Update checkout summary
        const summary = document.getElementById('checkoutSummary');
        summary.innerHTML = `
            <h4>订单详情 (${this.items.length} 项)</h4>
            <ul class="checkout-items">
                ${this.items.map(item => `
                    <li>
                        <span>${item.name}</span>
                        <span>$${item.price.toFixed(2)}</span>
                    </li>
                `).join('')}
            </ul>
            <div class="checkout-total">
                <strong>总计:</strong>
                <strong>$${this.getTotal().toFixed(2)}</strong>
            </div>
        `;

        document.getElementById('payAmount').textContent = '$' + this.getTotal().toFixed(2);
        document.getElementById('checkoutModal').classList.remove('hidden');
    },

    closeCheckout() {
        document.getElementById('checkoutModal').classList.add('hidden');
    },

    async handleCheckout(e) {
        e.preventDefault();

        const email = document.getElementById('customerEmail').value;
        const payBtn = document.getElementById('payBtn');

        payBtn.disabled = true;
        payBtn.textContent = '处理中...';

        // Simulate payment (in production, integrate with Stripe)
        await new Promise(resolve => setTimeout(resolve, 1500));

        // Show success
        this.closeCheckout();
        this.showSuccess(email);

        // Clear cart
        this.clear();

        payBtn.disabled = false;
        payBtn.innerHTML = '支付 <span id="payAmount">$0.00</span>';
    },

    showSuccess(email) {
        const links = document.getElementById('downloadLinks');
        links.innerHTML = this.items.map(item => `
            <a href="../${item.pdf}" class="download-link" download>
                📄 ${item.name}
            </a>
        `).join('');

        document.getElementById('successModal').classList.remove('hidden');
    },

    showNotification(message) {
        // Create notification
        const notif = document.createElement('div');
        notif.className = 'notification';
        notif.textContent = message;
        document.body.appendChild(notif);

        // Animate in
        setTimeout(() => notif.classList.add('show'), 10);

        // Remove after 2s
        setTimeout(() => {
            notif.classList.remove('show');
            setTimeout(() => notif.remove(), 300);
        }, 2000);
    }
};

// Initialize cart
document.addEventListener('DOMContentLoaded', () => Cart.init());
