// High-quality MIDI Player using Tone.js
class MidiPlayer {
    constructor() {
        this.synth = null;
        this.midiData = null;
        this.isPlaying = false;
        this.isPaused = false;
        this.currentTime = 0;
        this.duration = 0;
        this.scheduledEvents = [];
        this.onProgress = null;
        this.onEnd = null;
        this.initialized = false;
    }

    async init() {
        if (this.initialized) return;

        // Wait for Tone.js to load
        await Tone.start();

        // Create a high-quality piano sampler
        this.synth = new Tone.PolySynth(Tone.Synth, {
            oscillator: {
                type: 'triangle8'
            },
            envelope: {
                attack: 0.02,
                decay: 0.3,
                sustain: 0.4,
                release: 1.2
            },
            volume: -6
        }).toDestination();

        // Add reverb for richer sound
        const reverb = new Tone.Reverb({
            decay: 2.5,
            wet: 0.3
        }).toDestination();

        this.synth.connect(reverb);
        this.initialized = true;
    }

    async load(url) {
        try {
            const response = await fetch(url);
            const arrayBuffer = await response.arrayBuffer();
            this.midiData = new Midi(arrayBuffer);
            this.duration = this.midiData.duration;
            return true;
        } catch (error) {
            console.error('MIDI load error:', error);
            return false;
        }
    }

    async play() {
        if (!this.midiData || !this.initialized) {
            await this.init();
            if (!this.midiData) return;
        }

        if (this.isPaused) {
            this.isPaused = false;
            Tone.Transport.start();
            this.isPlaying = true;
            return;
        }

        this.stop();
        this.isPlaying = true;
        this.isPaused = false;

        const now = Tone.now();

        // Schedule all notes
        this.midiData.tracks.forEach(track => {
            track.notes.forEach(note => {
                const event = Tone.Transport.schedule(time => {
                    this.synth.triggerAttackRelease(
                        note.name,
                        note.duration,
                        time,
                        note.velocity
                    );
                }, note.time);
                this.scheduledEvents.push(event);
            });
        });

        // Progress tracking
        this.progressInterval = setInterval(() => {
            if (this.isPlaying && !this.isPaused) {
                this.currentTime = Tone.Transport.seconds;
                if (this.onProgress) {
                    this.onProgress(this.currentTime, this.duration);
                }
                if (this.currentTime >= this.duration) {
                    this.stop();
                    if (this.onEnd) this.onEnd();
                }
            }
        }, 100);

        Tone.Transport.start();
    }

    pause() {
        if (this.isPlaying) {
            this.isPaused = true;
            Tone.Transport.pause();
        }
    }

    stop() {
        this.isPlaying = false;
        this.isPaused = false;
        this.currentTime = 0;

        // Clear scheduled events
        this.scheduledEvents.forEach(event => {
            Tone.Transport.clear(event);
        });
        this.scheduledEvents = [];

        Tone.Transport.stop();
        Tone.Transport.position = 0;

        if (this.progressInterval) {
            clearInterval(this.progressInterval);
        }

        // Release all notes
        if (this.synth) {
            this.synth.releaseAll();
        }
    }

    seek(time) {
        const wasPlaying = this.isPlaying && !this.isPaused;
        this.stop();
        Tone.Transport.position = time;
        this.currentTime = time;
        if (wasPlaying) {
            this.play();
        }
    }

    setVolume(value) {
        // value: 0 to 1
        if (this.synth) {
            this.synth.volume.value = Tone.gainToDb(value);
        }
    }

    formatTime(seconds) {
        const mins = Math.floor(seconds / 60);
        const secs = Math.floor(seconds % 60);
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    }
}

// Global instance
window.midiPlayer = new MidiPlayer();
