/**
 * High-Quality MIDI Player using Tone.js
 * 使用钢琴采样实现高质量音色
 */

class MidiPlayer {
  constructor() {
    this.isPlaying = false;
    this.isPaused = false;
    this.currentMidi = null;
    this.sampler = null;
    this.scheduledEvents = [];
    this.startTime = 0;
    this.pauseTime = 0;
    this.duration = 0;
    this.progressInterval = null;
    this.initialized = false;
    this.volume = 0.8;

    // UI Elements
    this.ui = {
      container: null,
      playBtn: null,
      stopBtn: null,
      progressBar: null,
      progressFill: null,
      currentTime: null,
      durationEl: null,
      volumeSlider: null,
      loading: null
    };
  }

  async init() {
    if (this.initialized) return;

    // 显示加载状态
    this.showLoading(true);

    try {
      // 使用 Tone.js 钢琴采样器 - Salamander Grand Piano
      this.sampler = new Tone.Sampler({
        urls: {
          A0: "A0.mp3", C1: "C1.mp3", "D#1": "Ds1.mp3", "F#1": "Fs1.mp3",
          A1: "A1.mp3", C2: "C2.mp3", "D#2": "Ds2.mp3", "F#2": "Fs2.mp3",
          A2: "A2.mp3", C3: "C3.mp3", "D#3": "Ds3.mp3", "F#3": "Fs3.mp3",
          A3: "A3.mp3", C4: "C4.mp3", "D#4": "Ds4.mp3", "F#4": "Fs4.mp3",
          A4: "A4.mp3", C5: "C5.mp3", "D#5": "Ds5.mp3", "F#5": "Fs5.mp3",
          A5: "A5.mp3", C6: "C6.mp3", "D#6": "Ds6.mp3", "F#6": "Fs6.mp3",
          A6: "A6.mp3", C7: "C7.mp3", "D#7": "Ds7.mp3", "F#7": "Fs7.mp3",
          A7: "A7.mp3", C8: "C8.mp3"
        },
        release: 1,
        baseUrl: "https://tonejs.github.io/audio/salamander/"
      }).toDestination();

      // 等待采样器加载完成
      await Tone.loaded();

      // 添加混响效果
      this.reverb = new Tone.Reverb({
        decay: 2.5,
        wet: 0.3
      }).toDestination();

      this.sampler.connect(this.reverb);

      this.initialized = true;
      this.showLoading(false);
      console.log('MIDI Player initialized with Salamander Grand Piano');
    } catch (error) {
      console.error('Failed to initialize MIDI player:', error);
      this.showLoading(false);
    }
  }

  bindUI() {
    this.ui.container = document.getElementById('midiPlayerContainer');
    this.ui.playBtn = document.getElementById('midiPlayBtn');
    this.ui.stopBtn = document.getElementById('midiStopBtn');
    this.ui.progressBar = document.getElementById('midiProgressBar');
    this.ui.progressFill = document.getElementById('midiProgressFill');
    this.ui.currentTime = document.getElementById('midiCurrentTime');
    this.ui.durationEl = document.getElementById('midiDuration');
    this.ui.volumeSlider = document.getElementById('midiVolume');
    this.ui.loading = document.querySelector('.midi-loading');

    if (this.ui.playBtn) {
      this.ui.playBtn.addEventListener('click', () => this.togglePlay());
    }
    if (this.ui.stopBtn) {
      this.ui.stopBtn.addEventListener('click', () => this.stop());
    }
    if (this.ui.volumeSlider) {
      this.ui.volumeSlider.addEventListener('input', (e) => this.setVolume(e.target.value / 100));
    }
    if (this.ui.progressBar) {
      this.ui.progressBar.addEventListener('click', (e) => this.seek(e));
    }
  }

  async loadMidi(url) {
    this.stop();
    this.showLoading(true);

    try {
      // 确保已初始化
      if (!this.initialized) {
        await this.init();
      }

      // 加载 MIDI 文件
      const response = await fetch(url);
      const arrayBuffer = await response.arrayBuffer();
      this.currentMidi = new Midi(arrayBuffer);

      // 计算总时长
      this.duration = this.currentMidi.duration;
      this.updateTimeDisplay(0, this.duration);

      this.showLoading(false);
      this.show();
      console.log('MIDI loaded:', url, 'Duration:', this.duration);

      return true;
    } catch (error) {
      console.error('Failed to load MIDI:', error);
      this.showLoading(false);
      return false;
    }
  }

  async play() {
    if (!this.currentMidi || !this.sampler) return;

    // 用户交互后启动 AudioContext
    await Tone.start();

    const now = Tone.now();
    this.startTime = now - this.pauseTime;

    // 调度所有音符
    this.currentMidi.tracks.forEach(track => {
      track.notes.forEach(note => {
        if (note.time >= this.pauseTime) {
          const eventId = Tone.Transport.schedule(time => {
            this.sampler.triggerAttackRelease(
              note.name,
              note.duration,
              time,
              note.velocity
            );
          }, note.time - this.pauseTime + now);
          this.scheduledEvents.push(eventId);
        }
      });
    });

    this.isPlaying = true;
    this.isPaused = false;
    this.updatePlayButton();
    this.startProgressUpdate();
  }

  pause() {
    this.pauseTime = Tone.now() - this.startTime;
    this.clearScheduledEvents();
    this.isPlaying = false;
    this.isPaused = true;
    this.updatePlayButton();
    this.stopProgressUpdate();
  }

  stop() {
    this.clearScheduledEvents();
    this.sampler?.releaseAll();
    this.isPlaying = false;
    this.isPaused = false;
    this.pauseTime = 0;
    this.updatePlayButton();
    this.stopProgressUpdate();
    this.updateProgress(0);
    this.updateTimeDisplay(0, this.duration);
  }

  togglePlay() {
    if (this.isPlaying) {
      this.pause();
    } else {
      this.play();
    }
  }

  clearScheduledEvents() {
    this.scheduledEvents.forEach(id => Tone.Transport.clear(id));
    this.scheduledEvents = [];
  }

  setVolume(value) {
    this.volume = value;
    if (this.sampler) {
      this.sampler.volume.value = Tone.gainToDb(value);
    }
  }

  seek(event) {
    if (!this.currentMidi) return;
    const rect = this.ui.progressBar.getBoundingClientRect();
    const percent = (event.clientX - rect.left) / rect.width;
    const seekTime = percent * this.duration;

    const wasPlaying = this.isPlaying;
    this.stop();
    this.pauseTime = seekTime;

    if (wasPlaying) {
      this.play();
    } else {
      this.updateProgress(percent * 100);
      this.updateTimeDisplay(seekTime, this.duration);
    }
  }

  startProgressUpdate() {
    this.stopProgressUpdate();
    this.progressInterval = setInterval(() => {
      if (!this.isPlaying) return;

      const currentTime = Tone.now() - this.startTime;
      const percent = (currentTime / this.duration) * 100;

      if (currentTime >= this.duration) {
        this.stop();
        return;
      }

      this.updateProgress(percent);
      this.updateTimeDisplay(currentTime, this.duration);
    }, 100);
  }

  stopProgressUpdate() {
    if (this.progressInterval) {
      clearInterval(this.progressInterval);
      this.progressInterval = null;
    }
  }

  updateProgress(percent) {
    if (this.ui.progressFill) {
      this.ui.progressFill.style.width = Math.min(100, percent) + '%';
    }
  }

  updateTimeDisplay(current, total) {
    if (this.ui.currentTime) {
      this.ui.currentTime.textContent = this.formatTime(current);
    }
    if (this.ui.durationEl) {
      this.ui.durationEl.textContent = this.formatTime(total);
    }
  }

  formatTime(seconds) {
    if (!seconds || isNaN(seconds)) return '0:00';
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  }

  updatePlayButton() {
    if (this.ui.playBtn) {
      const icon = this.ui.playBtn.querySelector('.midi-icon');
      if (icon) {
        icon.textContent = this.isPlaying ? '⏸' : '▶';
      }
      this.ui.playBtn.classList.toggle('playing', this.isPlaying);
    }
  }

  show() {
    if (this.ui.container) {
      this.ui.container.style.display = 'block';
    }
  }

  hide() {
    this.stop();
    if (this.ui.container) {
      this.ui.container.style.display = 'none';
    }
  }

  showLoading(show) {
    if (this.ui.loading) {
      this.ui.loading.style.display = show ? 'flex' : 'none';
    }
  }
}

// 全局实例
window.midiPlayer = new MidiPlayer();

// DOM 加载后绑定 UI
document.addEventListener('DOMContentLoaded', () => {
  window.midiPlayer.bindUI();
});
