const Database = require('better-sqlite3');
const path = require('path');
require('dotenv').config();

const dbPath = process.env.DB_PATH || './data/ezmusicstore.db';
const db = new Database(path.resolve(__dirname, '..', dbPath));

// Enable WAL mode for better performance
db.pragma('journal_mode = WAL');

// Initialize database schema
function initDatabase() {
    // Users table
    db.exec(`
        CREATE TABLE IF NOT EXISTS users (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            email TEXT UNIQUE NOT NULL,
            password_hash TEXT NOT NULL,
            name TEXT,
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
        )
    `);

    // Favorites table
    db.exec(`
        CREATE TABLE IF NOT EXISTS favorites (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            user_id INTEGER NOT NULL,
            composer_slug TEXT NOT NULL,
            score_path TEXT NOT NULL,
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
            UNIQUE(user_id, score_path)
        )
    `);

    // Orders table
    db.exec(`
        CREATE TABLE IF NOT EXISTS orders (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            user_id INTEGER,
            guest_email TEXT,
            stripe_session_id TEXT UNIQUE,
            stripe_payment_intent TEXT,
            status TEXT DEFAULT 'pending',
            total_amount INTEGER NOT NULL,
            currency TEXT DEFAULT 'usd',
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE SET NULL
        )
    `);

    // Order items table
    db.exec(`
        CREATE TABLE IF NOT EXISTS order_items (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            order_id INTEGER NOT NULL,
            composer_slug TEXT NOT NULL,
            score_path TEXT NOT NULL,
            score_name TEXT NOT NULL,
            price INTEGER NOT NULL,
            FOREIGN KEY (order_id) REFERENCES orders(id) ON DELETE CASCADE
        )
    `);

    // Purchases table (for quick lookup of user's purchased scores)
    db.exec(`
        CREATE TABLE IF NOT EXISTS purchases (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            user_id INTEGER,
            guest_email TEXT,
            order_id INTEGER NOT NULL,
            composer_slug TEXT NOT NULL,
            score_path TEXT NOT NULL,
            score_name TEXT NOT NULL,
            download_count INTEGER DEFAULT 0,
            max_downloads INTEGER DEFAULT 10,
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE SET NULL,
            FOREIGN KEY (order_id) REFERENCES orders(id) ON DELETE CASCADE
        )
    `);

    // Create indexes for better query performance
    db.exec(`
        CREATE INDEX IF NOT EXISTS idx_favorites_user ON favorites(user_id);
        CREATE INDEX IF NOT EXISTS idx_orders_user ON orders(user_id);
        CREATE INDEX IF NOT EXISTS idx_orders_status ON orders(status);
        CREATE INDEX IF NOT EXISTS idx_purchases_user ON purchases(user_id);
        CREATE INDEX IF NOT EXISTS idx_purchases_email ON purchases(guest_email);
        CREATE INDEX IF NOT EXISTS idx_purchases_score ON purchases(score_path);
    `);

    console.log('Database initialized successfully');
}

module.exports = { db, initDatabase };

const jwt = require('jsonwebtoken');
require('dotenv').config();

const JWT_SECRET = process.env.JWT_SECRET || 'dev-secret-change-me';

// Generate JWT token
function generateToken(user) {
    return jwt.sign(
        { id: user.id, email: user.email },
        JWT_SECRET,
        { expiresIn: process.env.JWT_EXPIRES_IN || '7d' }
    );
}

// Verify JWT token middleware
function authenticateToken(req, res, next) {
    const authHeader = req.headers['authorization'];
    const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

    if (!token) {
        return res.status(401).json({ error: 'Access token required' });
    }

    jwt.verify(token, JWT_SECRET, (err, decoded) => {
        if (err) {
            return res.status(403).json({ error: 'Invalid or expired token' });
        }
        req.user = decoded;
        next();
    });
}

// Optional auth - doesn't fail if no token, but attaches user if valid
function optionalAuth(req, res, next) {
    const authHeader = req.headers['authorization'];
    const token = authHeader && authHeader.split(' ')[1];

    if (!token) {
        req.user = null;
        return next();
    }

    jwt.verify(token, JWT_SECRET, (err, decoded) => {
        req.user = err ? null : decoded;
        next();
    });
}

module.exports = { generateToken, authenticateToken, optionalAuth };

const { db } = require('./database');
const bcrypt = require('bcryptjs');

class User {
    // Create new user
    static create(email, password, name = null) {
        const passwordHash = bcrypt.hashSync(password, 10);
        const stmt = db.prepare(`
            INSERT INTO users (email, password_hash, name)
            VALUES (?, ?, ?)
        `);

        try {
            const result = stmt.run(email.toLowerCase(), passwordHash, name);
            return this.findById(result.lastInsertRowid);
        } catch (err) {
            if (err.code === 'SQLITE_CONSTRAINT_UNIQUE') {
                throw new Error('Email already exists');
            }
            throw err;
        }
    }

    // Find user by ID
    static findById(id) {
        const stmt = db.prepare('SELECT id, email, name, created_at, updated_at FROM users WHERE id = ?');
        return stmt.get(id);
    }

    // Find user by email
    static findByEmail(email) {
        const stmt = db.prepare('SELECT * FROM users WHERE email = ?');
        return stmt.get(email.toLowerCase());
    }

    // Verify password
    static verifyPassword(user, password) {
        return bcrypt.compareSync(password, user.password_hash);
    }

    // Update user profile
    static update(id, updates) {
        const allowedFields = ['name', 'email'];
        const fields = [];
        const values = [];

        for (const [key, value] of Object.entries(updates)) {
            if (allowedFields.includes(key) && value !== undefined) {
                fields.push(`${key} = ?`);
                values.push(key === 'email' ? value.toLowerCase() : value);
            }
        }

        if (fields.length === 0) return this.findById(id);

        fields.push('updated_at = CURRENT_TIMESTAMP');
        values.push(id);

        const stmt = db.prepare(`UPDATE users SET ${fields.join(', ')} WHERE id = ?`);
        stmt.run(...values);
        return this.findById(id);
    }

    // Change password
    static changePassword(id, newPassword) {
        const passwordHash = bcrypt.hashSync(newPassword, 10);
        const stmt = db.prepare('UPDATE users SET password_hash = ?, updated_at = CURRENT_TIMESTAMP WHERE id = ?');
        stmt.run(passwordHash, id);
    }

    // Get user's purchase history
    static getPurchases(userId) {
        const stmt = db.prepare(`
            SELECT p.*, o.created_at as purchase_date
            FROM purchases p
            JOIN orders o ON p.order_id = o.id
            WHERE p.user_id = ?
            ORDER BY o.created_at DESC
        `);
        return stmt.all(userId);
    }

    // Check if user has purchased a score
    static hasPurchased(userId, scorePath) {
        const stmt = db.prepare(`
            SELECT 1 FROM purchases WHERE user_id = ? AND score_path = ? LIMIT 1
        `);
        return !!stmt.get(userId, scorePath);
    }
}

module.exports = User;

const { db } = require('./database');

class Favorite {
    // Add to favorites
    static add(userId, composerSlug, scorePath) {
        const stmt = db.prepare(`
            INSERT OR IGNORE INTO favorites (user_id, composer_slug, score_path)
            VALUES (?, ?, ?)
        `);
        const result = stmt.run(userId, composerSlug, scorePath);
        return result.changes > 0;
    }

    // Remove from favorites
    static remove(userId, scorePath) {
        const stmt = db.prepare(`
            DELETE FROM favorites WHERE user_id = ? AND score_path = ?
        `);
        const result = stmt.run(userId, scorePath);
        return result.changes > 0;
    }

    // Get all favorites for a user
    static getByUser(userId) {
        const stmt = db.prepare(`
            SELECT composer_slug, score_path, created_at
            FROM favorites
            WHERE user_id = ?
            ORDER BY created_at DESC
        `);
        return stmt.all(userId);
    }

    // Check if a score is favorited
    static isFavorited(userId, scorePath) {
        const stmt = db.prepare(`
            SELECT 1 FROM favorites WHERE user_id = ? AND score_path = ? LIMIT 1
        `);
        return !!stmt.get(userId, scorePath);
    }

    // Get favorite count for a score (for popularity)
    static getScoreFavoriteCount(scorePath) {
        const stmt = db.prepare(`
            SELECT COUNT(*) as count FROM favorites WHERE score_path = ?
        `);
        return stmt.get(scorePath).count;
    }

    // Batch check favorites (for displaying lists)
    static batchCheck(userId, scorePaths) {
        if (!scorePaths.length) return {};

        const placeholders = scorePaths.map(() => '?').join(',');
        const stmt = db.prepare(`
            SELECT score_path FROM favorites 
            WHERE user_id = ? AND score_path IN (${placeholders})
        `);

        const results = stmt.all(userId, ...scorePaths);
        const favorited = {};
        results.forEach(r => favorited[r.score_path] = true);
        return favorited;
    }
}

module.exports = Favorite;

const { db } = require('./database');

class Order {
    // Create new order
    static create({ userId = null, guestEmail = null, stripeSessionId, totalAmount, currency = 'usd', items }) {
        const insertOrder = db.prepare(`
            INSERT INTO orders (user_id, guest_email, stripe_session_id, total_amount, currency, status)
            VALUES (?, ?, ?, ?, ?, 'pending')
        `);

        const insertItem = db.prepare(`
            INSERT INTO order_items (order_id, composer_slug, score_path, score_name, price)
            VALUES (?, ?, ?, ?, ?)
        `);

        const transaction = db.transaction(() => {
            const result = insertOrder.run(userId, guestEmail, stripeSessionId, totalAmount, currency);
            const orderId = result.lastInsertRowid;

            for (const item of items) {
                insertItem.run(orderId, item.composerSlug, item.scorePath, item.scoreName, item.price);
            }

            return orderId;
        });

        const orderId = transaction();
        return this.findById(orderId);
    }

    // Find order by ID
    static findById(id) {
        const order = db.prepare('SELECT * FROM orders WHERE id = ?').get(id);
        if (!order) return null;

        order.items = db.prepare('SELECT * FROM order_items WHERE order_id = ?').all(id);
        return order;
    }

    // Find order by Stripe session ID
    static findByStripeSession(sessionId) {
        const order = db.prepare('SELECT * FROM orders WHERE stripe_session_id = ?').get(sessionId);
        if (!order) return null;

        order.items = db.prepare('SELECT * FROM order_items WHERE order_id = ?').all(order.id);
        return order;
    }

    // Update order status
    static updateStatus(id, status, paymentIntent = null) {
        const stmt = db.prepare(`
            UPDATE orders 
            SET status = ?, stripe_payment_intent = COALESCE(?, stripe_payment_intent), updated_at = CURRENT_TIMESTAMP
            WHERE id = ?
        `);
        stmt.run(status, paymentIntent, id);
        return this.findById(id);
    }

    // Get orders by user
    static getByUser(userId, limit = 50) {
        const orders = db.prepare(`
            SELECT * FROM orders WHERE user_id = ? ORDER BY created_at DESC LIMIT ?
        `).all(userId, limit);

        for (const order of orders) {
            order.items = db.prepare('SELECT * FROM order_items WHERE order_id = ?').all(order.id);
        }
        return orders;
    }

    // Get orders by guest email
    static getByEmail(email, limit = 50) {
        const orders = db.prepare(`
            SELECT * FROM orders WHERE guest_email = ? ORDER BY created_at DESC LIMIT ?
        `).all(email.toLowerCase(), limit);

        for (const order of orders) {
            order.items = db.prepare('SELECT * FROM order_items WHERE order_id = ?').all(order.id);
        }
        return orders;
    }

    // Fulfill order - create purchase records
    static fulfill(orderId) {
        const order = this.findById(orderId);
        if (!order || order.status === 'fulfilled') return order;

        const insertPurchase = db.prepare(`
            INSERT INTO purchases (user_id, guest_email, order_id, composer_slug, score_path, score_name)
            VALUES (?, ?, ?, ?, ?, ?)
        `);

        const transaction = db.transaction(() => {
            for (const item of order.items) {
                insertPurchase.run(
                    order.user_id,
                    order.guest_email,
                    orderId,
                    item.composer_slug,
                    item.score_path,
                    item.score_name
                );
            }
            this.updateStatus(orderId, 'fulfilled');
        });

        transaction();
        return this.findById(orderId);
    }
}

module.exports = Order;

const express = require('express');
const router = express.Router();
const User = require('../models/User');
const { generateToken, authenticateToken } = require('../middleware/auth');

// POST /api/auth/register
router.post('/register', (req, res) => {
    try {
        const { email, password, name } = req.body;

        // Validation
        if (!email || !password) {
            return res.status(400).json({ error: 'Email and password are required' });
        }

        if (password.length < 6) {
            return res.status(400).json({ error: 'Password must be at least 6 characters' });
        }

        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            return res.status(400).json({ error: 'Invalid email format' });
        }

        // Create user
        const user = User.create(email, password, name);
        const token = generateToken(user);

        res.status(201).json({
            message: 'Registration successful',
            user: {
                id: user.id,
                email: user.email,
                name: user.name
            },
            token
        });
    } catch (err) {
        if (err.message === 'Email already exists') {
            return res.status(409).json({ error: 'Email already registered' });
        }
        console.error('Registration error:', err);
        res.status(500).json({ error: 'Registration failed' });
    }
});

// POST /api/auth/login
router.post('/login', (req, res) => {
    try {
        const { email, password } = req.body;

        if (!email || !password) {
            return res.status(400).json({ error: 'Email and password are required' });
        }

        const user = User.findByEmail(email);
        if (!user) {
            return res.status(401).json({ error: 'Invalid email or password' });
        }

        if (!User.verifyPassword(user, password)) {
            return res.status(401).json({ error: 'Invalid email or password' });
        }

        const token = generateToken(user);

        res.json({
            message: 'Login successful',
            user: {
                id: user.id,
                email: user.email,
                name: user.name
            },
            token
        });
    } catch (err) {
        console.error('Login error:', err);
        res.status(500).json({ error: 'Login failed' });
    }
});

// GET /api/auth/me
router.get('/me', authenticateToken, (req, res) => {
    try {
        const user = User.findById(req.user.id);
        if (!user) {
            return res.status(404).json({ error: 'User not found' });
        }

        res.json({
            id: user.id,
            email: user.email,
            name: user.name,
            created_at: user.created_at
        });
    } catch (err) {
        console.error('Get user error:', err);
        res.status(500).json({ error: 'Failed to get user info' });
    }
});

// PUT /api/auth/profile
router.put('/profile', authenticateToken, (req, res) => {
    try {
        const { name, email } = req.body;
        const user = User.update(req.user.id, { name, email });

        res.json({
            message: 'Profile updated',
            user: {
                id: user.id,
                email: user.email,
                name: user.name
            }
        });
    } catch (err) {
        if (err.message.includes('UNIQUE constraint')) {
            return res.status(409).json({ error: 'Email already in use' });
        }
        console.error('Update profile error:', err);
        res.status(500).json({ error: 'Failed to update profile' });
    }
});

// PUT /api/auth/password
router.put('/password', authenticateToken, (req, res) => {
    try {
        const { currentPassword, newPassword } = req.body;

        if (!currentPassword || !newPassword) {
            return res.status(400).json({ error: 'Current and new password are required' });
        }

        if (newPassword.length < 6) {
            return res.status(400).json({ error: 'New password must be at least 6 characters' });
        }

        const user = User.findByEmail(req.user.email);
        if (!User.verifyPassword(user, currentPassword)) {
            return res.status(401).json({ error: 'Current password is incorrect' });
        }

        User.changePassword(req.user.id, newPassword);
        res.json({ message: 'Password changed successfully' });
    } catch (err) {
        console.error('Change password error:', err);
        res.status(500).json({ error: 'Failed to change password' });
    }
});

module.exports = router;

const express = require('express');
const router = express.Router();
const Favorite = require('../models/Favorite');
const { authenticateToken } = require('../middleware/auth');

// All favorite routes require authentication
router.use(authenticateToken);

// GET /api/favorites - Get user's favorites
router.get('/', (req, res) => {
    try {
        const favorites = Favorite.getByUser(req.user.id);
        res.json({ favorites });
    } catch (err) {
        console.error('Get favorites error:', err);
        res.status(500).json({ error: 'Failed to get favorites' });
    }
});

// POST /api/favorites - Add to favorites
router.post('/', (req, res) => {
    try {
        const { composerSlug, scorePath } = req.body;

        if (!composerSlug || !scorePath) {
            return res.status(400).json({ error: 'composerSlug and scorePath are required' });
        }

        const added = Favorite.add(req.user.id, composerSlug, scorePath);

        res.status(added ? 201 : 200).json({
            message: added ? 'Added to favorites' : 'Already in favorites',
            favorited: true
        });
    } catch (err) {
        console.error('Add favorite error:', err);
        res.status(500).json({ error: 'Failed to add favorite' });
    }
});

// DELETE /api/favorites - Remove from favorites
router.delete('/', (req, res) => {
    try {
        const { scorePath } = req.body;

        if (!scorePath) {
            return res.status(400).json({ error: 'scorePath is required' });
        }

        const removed = Favorite.remove(req.user.id, scorePath);

        res.json({
            message: removed ? 'Removed from favorites' : 'Was not in favorites',
            favorited: false
        });
    } catch (err) {
        console.error('Remove favorite error:', err);
        res.status(500).json({ error: 'Failed to remove favorite' });
    }
});

// POST /api/favorites/check - Check if score is favorited
router.post('/check', (req, res) => {
    try {
        const { scorePath } = req.body;

        if (!scorePath) {
            return res.status(400).json({ error: 'scorePath is required' });
        }

        const favorited = Favorite.isFavorited(req.user.id, scorePath);
        res.json({ favorited });
    } catch (err) {
        console.error('Check favorite error:', err);
        res.status(500).json({ error: 'Failed to check favorite' });
    }
});

// POST /api/favorites/batch-check - Check multiple scores
router.post('/batch-check', (req, res) => {
    try {
        const { scorePaths } = req.body;

        if (!Array.isArray(scorePaths)) {
            return res.status(400).json({ error: 'scorePaths must be an array' });
        }

        const favorited = Favorite.batchCheck(req.user.id, scorePaths);
        res.json({ favorited });
    } catch (err) {
        console.error('Batch check favorites error:', err);
        res.status(500).json({ error: 'Failed to check favorites' });
    }
});

module.exports = router;

