// POST /api/orders/check-purchased - Check if user has purchased scores
router.post('/check-purchased', optionalAuth, (req, res) => {
    try {
        const { scorePaths, email } = req.body;

        if (!Array.isArray(scorePaths)) {
            return res.status(400).json({ error: 'scorePaths must be an array' });
        }

        const purchased = {};

        if (req.user) {
            // Check for logged-in user
            scorePaths.forEach(path => {
                purchased[path] = User.hasPurchased(req.user.id, path);
            });
        } else if (email) {
            // Check for guest by email
            const { db } = require('../models/database');
            const stmt = db.prepare(`
                SELECT score_path FROM purchases WHERE guest_email = ? AND score_path IN (${scorePaths.map(() => '?').join(',')})
            `);
            const results = stmt.all(email.toLowerCase(), ...scorePaths);
            scorePaths.forEach(path => purchased[path] = false);
            results.forEach(r => purchased[r.score_path] = true);
        } else {
            scorePaths.forEach(path => purchased[path] = false);
        }

        res.json({ purchased });
    } catch (err) {
        console.error('Check purchased error:', err);
        res.status(500).json({ error: 'Failed to check purchases' });
    }
});

