const express = require('express');
const router = express.Router();
const Order = require('../models/Order');
const { optionalAuth } = require('../middleware/auth');
require('dotenv').config();

const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

// Price in cents
const SCORE_PRICE = 299; // $2.99

// POST /api/checkout/create-session - Create Stripe checkout session
router.post('/create-session', optionalAuth, async (req, res) => {
    try {
        const { items, email } = req.body;

        if (!items || !Array.isArray(items) || items.length === 0) {
            return res.status(400).json({ error: 'Cart items are required' });
        }

        // Build line items for Stripe
        const lineItems = items.map(item => ({
            price_data: {
                currency: 'usd',
                product_data: {
                    name: item.scoreName,
                    description: `Piano score by ${item.composerName || item.composerSlug}`,
                    metadata: {
                        composer_slug: item.composerSlug,
                        score_path: item.scorePath
                    }
                },
                unit_amount: SCORE_PRICE
            },
            quantity: 1
        }));

        // Calculate total
        const totalAmount = items.length * SCORE_PRICE;

        // Create Stripe checkout session
        const session = await stripe.checkout.sessions.create({
            payment_method_types: ['card'],
            line_items: lineItems,
            mode: 'payment',
            success_url: `${process.env.FRONTEND_URL || 'http://localhost:8080'}?success=true&session_id={CHECKOUT_SESSION_ID}`,
            cancel_url: `${process.env.FRONTEND_URL || 'http://localhost:8080'}?canceled=true`,
            customer_email: email || (req.user ? req.user.email : undefined),
            metadata: {
                user_id: req.user ? req.user.id.toString() : '',
                item_count: items.length.toString()
            }
        });

        // Create pending order in database
        const orderItems = items.map(item => ({
            composerSlug: item.composerSlug,
            scorePath: item.scorePath,
            scoreName: item.scoreName,
            price: SCORE_PRICE
        }));

        Order.create({
            userId: req.user ? req.user.id : null,
            guestEmail: email || null,
            stripeSessionId: session.id,
            totalAmount,
            items: orderItems
        });

        res.json({
            sessionId: session.id,
            url: session.url
        });
    } catch (err) {
        console.error('Create checkout session error:', err);
        res.status(500).json({ error: 'Failed to create checkout session' });
    }
});

// GET /api/checkout/session/:sessionId - Get session status
router.get('/session/:sessionId', async (req, res) => {
    try {
        const session = await stripe.checkout.sessions.retrieve(req.params.sessionId);
        const order = Order.findByStripeSession(req.params.sessionId);

        res.json({
            status: session.payment_status,
            customerEmail: session.customer_email,
            order: order ? {
                id: order.id,
                status: order.status,
                items: order.items
            } : null
        });
    } catch (err) {
        console.error('Get session error:', err);
        res.status(500).json({ error: 'Failed to get session status' });
    }
});

module.exports = router;

const express = require('express');
const router = express.Router();
const Order = require('../models/Order');
require('dotenv').config();

const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);
const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;

// POST /api/webhook/stripe - Handle Stripe webhooks
// Note: This route needs raw body, configured in main app
router.post('/stripe', async (req, res) => {
    const sig = req.headers['stripe-signature'];
    let event;

    try {
        // Verify webhook signature
        if (webhookSecret) {
            event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
        } else {
            // For development without webhook secret
            event = req.body;
            console.warn('⚠️ Webhook signature verification skipped (no secret configured)');
        }
    } catch (err) {
        console.error('Webhook signature verification failed:', err.message);
        return res.status(400).send(`Webhook Error: ${err.message}`);
    }

    // Handle the event
    switch (event.type) {
        case 'checkout.session.completed': {
            const session = event.data.object;
            console.log('✅ Payment successful for session:', session.id);

            // Find and fulfill the order
            const order = Order.findByStripeSession(session.id);
            if (order) {
                Order.updateStatus(order.id, 'paid', session.payment_intent);
                Order.fulfill(order.id);
                console.log(`✅ Order ${order.id} fulfilled`);
            } else {
                console.error('❌ Order not found for session:', session.id);
            }
            break;
        }

        case 'checkout.session.expired': {
            const session = event.data.object;
            console.log('⏰ Session expired:', session.id);

            const order = Order.findByStripeSession(session.id);
            if (order) {
                Order.updateStatus(order.id, 'expired');
            }
            break;
        }

        case 'payment_intent.payment_failed': {
            const paymentIntent = event.data.object;
            console.log('❌ Payment failed:', paymentIntent.id);
            break;
        }

        default:
            console.log(`Unhandled event type: ${event.type}`);
    }

    res.json({ received: true });
});

module.exports = router;

const express = require('express');
const router = express.Router();
const path = require('path');
const fs = require('fs');
const { db } = require('../models/database');
const { optionalAuth } = require('../middleware/auth');

// Base path for score files
const SCORES_BASE_PATH = path.resolve(__dirname, '../../scores');

// GET /api/download/:purchaseId - Download a purchased score
router.get('/:purchaseId', optionalAuth, async (req, res) => {
    try {
        const purchaseId = parseInt(req.params.purchaseId);

        // Get purchase record
        const purchase = db.prepare(`
            SELECT * FROM purchases WHERE id = ?
        `).get(purchaseId);

        if (!purchase) {
            return res.status(404).json({ error: 'Purchase not found' });
        }

        // Verify ownership
        if (req.user) {
            // Logged in user
            if (purchase.user_id !== req.user.id) {
                return res.status(403).json({ error: 'Access denied' });
            }
        } else {
            // Guest - need email verification (simplified for now)
            return res.status(401).json({ error: 'Please log in to download' });
        }

        // Check download limit
        if (purchase.download_count >= purchase.max_downloads) {
            return res.status(403).json({ error: 'Download limit reached' });
        }

        // Build file path
        const filePath = path.join(SCORES_BASE_PATH, purchase.score_path);

        // Check if file exists
        if (!fs.existsSync(filePath)) {
            console.error('File not found:', filePath);
            return res.status(404).json({ error: 'File not found' });
        }

        // Increment download count
        db.prepare(`
            UPDATE purchases SET download_count = download_count + 1 WHERE id = ?
        `).run(purchaseId);

        // Send file
        const fileName = path.basename(purchase.score_path);
        res.download(filePath, fileName);

    } catch (err) {
        console.error('Download error:', err);
        res.status(500).json({ error: 'Download failed' });
    }
});

// GET /api/download/midi/:purchaseId - Download MIDI file for purchased score
router.get('/midi/:purchaseId', optionalAuth, async (req, res) => {
    try {
        const purchaseId = parseInt(req.params.purchaseId);

        const purchase = db.prepare(`
            SELECT * FROM purchases WHERE id = ?
        `).get(purchaseId);

        if (!purchase) {
            return res.status(404).json({ error: 'Purchase not found' });
        }

        // Verify ownership
        if (!req.user || purchase.user_id !== req.user.id) {
            return res.status(403).json({ error: 'Access denied' });
        }

        // Build MIDI path (replace .pdf with .mid)
        const midiPath = purchase.score_path.replace(/\.pdf$/i, '.mid');
        const filePath = path.join(SCORES_BASE_PATH, midiPath);

        if (!fs.existsSync(filePath)) {
            return res.status(404).json({ error: 'MIDI file not available for this score' });
        }

        const fileName = path.basename(midiPath);
        res.download(filePath, fileName);

    } catch (err) {
        console.error('MIDI download error:', err);
        res.status(500).json({ error: 'Download failed' });
    }
});

// POST /api/download/verify - Verify download access without downloading
router.post('/verify', optionalAuth, (req, res) => {
    try {
        const { scorePath } = req.body;

        if (!scorePath) {
            return res.status(400).json({ error: 'scorePath is required' });
        }

        if (!req.user) {
            return res.json({ canDownload: false, reason: 'not_logged_in' });
        }

        const purchase = db.prepare(`
            SELECT * FROM purchases WHERE user_id = ? AND score_path = ?
        `).get(req.user.id, scorePath);

        if (!purchase) {
            return res.json({ canDownload: false, reason: 'not_purchased' });
        }

        if (purchase.download_count >= purchase.max_downloads) {
            return res.json({ canDownload: false, reason: 'limit_reached' });
        }

        res.json({
            canDownload: true,
            purchaseId: purchase.id,
            downloadsRemaining: purchase.max_downloads - purchase.download_count
        });

    } catch (err) {
        console.error('Verify download error:', err);
        res.status(500).json({ error: 'Verification failed' });
    }
});

module.exports = router;

const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const rateLimit = require('express-rate-limit');
const path = require('path');
require('dotenv').config();

const { initDatabase } = require('./models/database');

// Initialize database
initDatabase();

const app = express();
const PORT = process.env.PORT || 3000;

// Trust proxy (for rate limiting behind reverse proxy)
app.set('trust proxy', 1);

// Security middleware
app.use(helmet({
    crossOriginResourcePolicy: { policy: 'cross-origin' }
}));

// CORS
app.use(cors({
    origin: process.env.FRONTEND_URL || 'http://localhost:8080',
    credentials: true
}));

// Rate limiting
const limiter = rateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 100, // limit each IP to 100 requests per windowMs
    message: { error: 'Too many requests, please try again later' }
});
app.use('/api/', limiter);

// Stricter rate limit for auth routes
const authLimiter = rateLimit({
    windowMs: 15 * 60 * 1000,
    max: 20,
    message: { error: 'Too many authentication attempts, please try again later' }
});
app.use('/api/auth/', authLimiter);

// Webhook route needs raw body for signature verification
const webhookRouter = require('./routes/webhook');
app.use('/api/webhook', express.raw({ type: 'application/json' }), webhookRouter);

// JSON body parser for other routes
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// API Routes
app.use('/api/auth', require('./routes/auth'));
app.use('/api/favorites', require('./routes/favorites'));
app.use('/api/orders', require('./routes/orders'));
app.use('/api/checkout', require('./routes/checkout'));
app.use('/api/download', require('./routes/download'));

// Health check
app.get('/api/health', (req, res) => {
    res.json({ status: 'ok', timestamp: new Date().toISOString() });
});

// Serve static files (frontend)
const staticPath = path.join(__dirname, '../site');
app.use(express.static(staticPath));

// Serve scores for preview (PDF viewing) - but not direct download
const scoresPath = path.join(__dirname, '../scores');
app.use('/scores', express.static(scoresPath));

// SPA fallback
app.get('*', (req, res) => {
    res.sendFile(path.join(staticPath, 'index.html'));
});

// Error handling middleware
app.use((err, req, res, next) => {
    console.error('Server error:', err);
    res.status(500).json({ error: 'Internal server error' });
});

// Start server
app.listen(PORT, () => {
    console.log(`\n🎹 EzMusicStore Server running on http://localhost:${PORT}`);
    console.log(`📁 Serving frontend from: ${staticPath}`);
    console.log(`🎵 Scores directory: ${scoresPath}`);
    console.log(`🔧 Environment: ${process.env.NODE_ENV || 'development'}\n`);
});

module.exports = app;

