// API Client for EzMusicStore
const API = {
    baseUrl: window.location.hostname === 'localhost' 
        ? 'http://localhost:3000/api' 
        : '/api',

    token: localStorage.getItem('auth_token'),
    user: JSON.parse(localStorage.getItem('auth_user') || 'null'),

    // Set auth token
    setAuth(token, user) {
        this.token = token;
        this.user = user;
        if (token) {
            localStorage.setItem('auth_token', token);
            localStorage.setItem('auth_user', JSON.stringify(user));
        } else {
            localStorage.removeItem('auth_token');
            localStorage.removeItem('auth_user');
        }
        window.dispatchEvent(new CustomEvent('authChanged', { detail: { user } }));
    },

    // Clear auth
    logout() {
        this.setAuth(null, null);
    },

    // Check if logged in
    isLoggedIn() {
        return !!this.token;
    },

    // Get current user
    getUser() {
        return this.user;
    },

    // Make API request
    async request(endpoint, options = {}) {
        const url = `${this.baseUrl}${endpoint}`;
        const headers = {
            'Content-Type': 'application/json',
            ...options.headers
        };

        if (this.token) {
            headers['Authorization'] = `Bearer ${this.token}`;
        }

        try {
            const response = await fetch(url, {
                ...options,
                headers
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Request failed');
            }

            return data;
        } catch (err) {
            console.error('API Error:', err);
            throw err;
        }
    },

    // Auth endpoints
    auth: {
        async register(email, password, name) {
            const data = await API.request('/auth/register', {
                method: 'POST',
                body: JSON.stringify({ email, password, name })
            });
            API.setAuth(data.token, data.user);
            return data;
        },

        async login(email, password) {
            const data = await API.request('/auth/login', {
                method: 'POST',
                body: JSON.stringify({ email, password })
            });
            API.setAuth(data.token, data.user);
            return data;
        },

        async getProfile() {
            return API.request('/auth/me');
        },

        async updateProfile(updates) {
            const data = await API.request('/auth/profile', {
                method: 'PUT',
                body: JSON.stringify(updates)
            });
            API.setAuth(API.token, data.user);
            return data;
        },

        async changePassword(currentPassword, newPassword) {
            return API.request('/auth/password', {
                method: 'PUT',
                body: JSON.stringify({ currentPassword, newPassword })
            });
        }
    },

    // Favorites endpoints
    favorites: {
        async list() {
            return API.request('/favorites');
        },

        async add(composerSlug, scorePath) {
            return API.request('/favorites', {
                method: 'POST',
                body: JSON.stringify({ composerSlug, scorePath })
            });
        },

        async remove(scorePath) {
            return API.request('/favorites', {
                method: 'DELETE',
                body: JSON.stringify({ scorePath })
            });
        },

        async check(scorePath) {
            return API.request('/favorites/check', {
                method: 'POST',
                body: JSON.stringify({ scorePath })
            });
        },

        async batchCheck(scorePaths) {
            return API.request('/favorites/batch-check', {
                method: 'POST',
                body: JSON.stringify({ scorePaths })
            });
        }
    },

    // Orders endpoints
    orders: {
        async list() {
            return API.request('/orders');
        },

        async get(orderId) {
            return API.request(`/orders/${orderId}`);
        },

        async getPurchases() {
            return API.request('/orders/purchases/list');
        },

        async checkPurchased(scorePaths, email = null) {
            return API.request('/orders/check-purchased', {
                method: 'POST',
                body: JSON.stringify({ scorePaths, email })
            });
        }
    },

    // Checkout endpoints
    checkout: {
        async createSession(items, email = null) {
            return API.request('/checkout/create-session', {
                method: 'POST',
                body: JSON.stringify({ items, email })
            });
        },

        async getSession(sessionId) {
            return API.request(`/checkout/session/${sessionId}`);
        }
    },

    // Download endpoints
    download: {
        async verify(scorePath) {
            return API.request('/download/verify', {
                method: 'POST',
                body: JSON.stringify({ scorePath })
            });
        },

        getUrl(purchaseId) {
            return `${API.baseUrl}/download/${purchaseId}`;
        },

        getMidiUrl(purchaseId) {
            return `${API.baseUrl}/download/midi/${purchaseId}`;
        }
    }
};

// Initialize - verify token on load
if (API.token) {
    API.auth.getProfile().catch(() => {
        // Token invalid, clear auth
        API.logout();
    });
}

// Auth UI Component
const AuthUI = {
    modal: null,

    init() {
        this.createModal();
        this.bindEvents();
        this.updateHeaderUI();

        // Listen for auth changes
        window.addEventListener('authChanged', () => this.updateHeaderUI());
    },

    createModal() {
        const modal = document.createElement('div');
        modal.id = 'authModal';
        modal.className = 'modal hidden';
        modal.innerHTML = `
            <div class="modal-content auth-content">
                <button class="btn-close auth-close">&times;</button>

                <div class="auth-tabs">
                    <button class="auth-tab active" data-tab="login">登录</button>
                    <button class="auth-tab" data-tab="register">注册</button>
                </div>

                <form id="loginForm" class="auth-form">
                    <h2>欢迎回来</h2>
                    <div class="form-group">
                        <label for="loginEmail">邮箱</label>
                        <input type="email" id="loginEmail" required placeholder="your@email.com">
                    </div>
                    <div class="form-group">
                        <label for="loginPassword">密码</label>
                        <input type="password" id="loginPassword" required placeholder="输入密码">
                    </div>
                    <div class="auth-error hidden" id="loginError"></div>
                    <button type="submit" class="btn btn-primary btn-block">登录</button>
                </form>

                <form id="registerForm" class="auth-form hidden">
                    <h2>创建账户</h2>
                    <div class="form-group">
                        <label for="registerName">姓名 (可选)</label>
                        <input type="text" id="registerName" placeholder="您的姓名">
                    </div>
                    <div class="form-group">
                        <label for="registerEmail">邮箱</label>
                        <input type="email" id="registerEmail" required placeholder="your@email.com">
                    </div>
                    <div class="form-group">
                        <label for="registerPassword">密码</label>
                        <input type="password" id="registerPassword" required placeholder="至少6位" minlength="6">
                    </div>
                    <div class="form-group">
                        <label for="registerConfirm">确认密码</label>
                        <input type="password" id="registerConfirm" required placeholder="再次输入密码">
                    </div>
                    <div class="auth-error hidden" id="registerError"></div>
                    <button type="submit" class="btn btn-primary btn-block">注册</button>
                </form>
            </div>
        `;
        document.body.appendChild(modal);
        this.modal = modal;
    },

    bindEvents() {
        // Close button
        this.modal.querySelector('.auth-close').addEventListener('click', () => this.hide());

        // Click outside to close
        this.modal.addEventListener('click', (e) => {
            if (e.target === this.modal) this.hide();
        });

        // Tab switching
        this.modal.querySelectorAll('.auth-tab').forEach(tab => {
            tab.addEventListener('click', () => this.switchTab(tab.dataset.tab));
        });

        // Login form
        document.getElementById('loginForm').addEventListener('submit', (e) => this.handleLogin(e));

        // Register form
        document.getElementById('registerForm').addEventListener('submit', (e) => this.handleRegister(e));
    },

    switchTab(tab) {
        // Update tabs
        this.modal.querySelectorAll('.auth-tab').forEach(t => {
            t.classList.toggle('active', t.dataset.tab === tab);
        });

        // Show/hide forms
        document.getElementById('loginForm').classList.toggle('hidden', tab !== 'login');
        document.getElementById('registerForm').classList.toggle('hidden', tab !== 'register');

        // Clear errors
        this.modal.querySelectorAll('.auth-error').forEach(el => el.classList.add('hidden'));
    },

    async handleLogin(e) {
        e.preventDefault();
        const email = document.getElementById('loginEmail').value;
        const password = document.getElementById('loginPassword').value;
        const errorEl = document.getElementById('loginError');
        const btn = e.target.querySelector('button[type="submit"]');

        btn.disabled = true;
        btn.textContent = '登录中...';
        errorEl.classList.add('hidden');

        try {
            await API.auth.login(email, password);
            this.hide();
            this.showToast('登录成功！', 'success');
        } catch (err) {
            errorEl.textContent = err.message;
            errorEl.classList.remove('hidden');
        } finally {
            btn.disabled = false;
            btn.textContent = '登录';
        }
    },

    async handleRegister(e) {
        e.preventDefault();
        const name = document.getElementById('registerName').value;
        const email = document.getElementById('registerEmail').value;
        const password = document.getElementById('registerPassword').value;
        const confirm = document.getElementById('registerConfirm').value;
        const errorEl = document.getElementById('registerError');
        const btn = e.target.querySelector('button[type="submit"]');

        if (password !== confirm) {
            errorEl.textContent = '两次输入的密码不一致';
            errorEl.classList.remove('hidden');
            return;
        }

        btn.disabled = true;
        btn.textContent = '注册中...';
        errorEl.classList.add('hidden');

        try {
            await API.auth.register(email, password, name || undefined);
            this.hide();
            this.showToast('注册成功！欢迎加入 EzMusicStore', 'success');
        } catch (err) {
            errorEl.textContent = err.message;
            errorEl.classList.remove('hidden');
        } finally {
            btn.disabled = false;
            btn.textContent = '注册';
        }
    },

    show(tab = 'login') {
        this.switchTab(tab);
        this.modal.classList.remove('hidden');
        // Focus first input
        setTimeout(() => {
            const form = tab === 'login' ? 'loginForm' : 'registerForm';
            document.getElementById(form).querySelector('input').focus();
        }, 100);
    },

    hide() {
        this.modal.classList.add('hidden');
        // Clear forms
        document.getElementById('loginForm').reset();
        document.getElementById('registerForm').reset();
        this.modal.querySelectorAll('.auth-error').forEach(el => el.classList.add('hidden'));
    },

    updateHeaderUI() {
        const headerRight = document.querySelector('.header-right');
        const user = API.getUser();

        // Remove existing user menu if any
        const existingMenu = headerRight.querySelector('.user-menu');
        if (existingMenu) existingMenu.remove();

        const existingBtn = headerRight.querySelector('.login-btn');
        if (existingBtn) existingBtn.remove();

        if (user) {
            // Show user menu
            const menu = document.createElement('div');
            menu.className = 'user-menu';
            menu.innerHTML = `
                <button class="user-btn">
                    <span class="user-avatar">${(user.name || user.email)[0].toUpperCase()}</span>
                    <span class="user-name">${user.name || user.email.split('@')[0]}</span>
                </button>
                <div class="user-dropdown hidden">
                    <a href="#" class="dropdown-item" data-action="library">📚 我的乐谱</a>
                    <a href="#" class="dropdown-item" data-action="favorites">❤️ 收藏夹</a>
                    <a href="#" class="dropdown-item" data-action="orders">📋 订单历史</a>
                    <hr>
                    <a href="#" class="dropdown-item" data-action="logout">退出登录</a>
                </div>
            `;
            headerRight.insertBefore(menu, headerRight.firstChild);

            // Toggle dropdown
            const btn = menu.querySelector('.user-btn');
            const dropdown = menu.querySelector('.user-dropdown');
            btn.addEventListener('click', (e) => {
                e.stopPropagation();
                dropdown.classList.toggle('hidden');
            });

            // Close on outside click
            document.addEventListener('click', () => dropdown.classList.add('hidden'));

            // Handle menu actions
            menu.querySelectorAll('.dropdown-item').forEach(item => {
                item.addEventListener('click', (e) => {
                    e.preventDefault();
                    dropdown.classList.add('hidden');
                    this.handleMenuAction(item.dataset.action);
                });
            });
        } else {
            // Show login button
            const loginBtn = document.createElement('button');
            loginBtn.className = 'login-btn';
            loginBtn.textContent = '登录';
            loginBtn.addEventListener('click', () => this.show('login'));
            headerRight.insertBefore(loginBtn, headerRight.firstChild);
        }
    },

    handleMenuAction(action) {
        switch (action) {
            case 'library':
                UserPages.showLibrary();
                break;
            case 'favorites':
                UserPages.showFavorites();
                break;
            case 'orders':
                UserPages.showOrders();
                break;
            case 'logout':
                API.logout();
                this.showToast('已退出登录', 'info');
                break;
        }
    },

    showToast(message, type = 'info') {
        const toast = document.createElement('div');
        toast.className = `toast toast-${type}`;
        toast.textContent = message;
        document.body.appendChild(toast);

        setTimeout(() => toast.classList.add('show'), 10);
        setTimeout(() => {
            toast.classList.remove('show');
            setTimeout(() => toast.remove(), 300);
        }, 3000);
    }
};

// Initialize when DOM ready
document.addEventListener('DOMContentLoaded', () => AuthUI.init());

// User Pages Component
const UserPages = {
    container: null,

    init() {
        this.createContainer();
    },

    createContainer() {
        const container = document.createElement('section');
        container.id = 'userPages';
        container.className = 'user-pages hidden';
        container.innerHTML = `
            <div class="breadcrumb">
                <a href="#" id="backFromUser">← 返回首页</a>
                <h2 id="userPageTitle"></h2>
            </div>
            <div id="userPageContent" class="user-page-content"></div>
        `;
        document.querySelector('.main').appendChild(container);
        this.container = container;

        document.getElementById('backFromUser').addEventListener('click', (e) => {
            e.preventDefault();
            this.hide();
        });
    },

    show(title) {
        document.getElementById('userPageTitle').textContent = title;

        // Hide other sections
        document.getElementById('featuredSection').classList.add('hidden');
        document.getElementById('composerList').classList.add('hidden');
        document.getElementById('scoreList').classList.add('hidden');
        document.getElementById('searchResults').classList.add('hidden');

        this.container.classList.remove('hidden');
    },

    hide() {
        this.container.classList.add('hidden');
        document.getElementById('featuredSection').classList.remove('hidden');
        document.getElementById('composerList').classList.remove('hidden');
    },

    // My Library - Purchased scores
    async showLibrary() {
        if (!API.isLoggedIn()) {
            AuthUI.show('login');
            return;
        }

        this.show('我的乐谱库');
        const content = document.getElementById('userPageContent');
        content.innerHTML = '<div class="loading">加载中...</div>';

        try {
            const { purchases } = await API.orders.getPurchases();

            if (purchases.length === 0) {
                content.innerHTML = `
                    <div class="empty-state">
                        <div class="empty-icon">📚</div>
                        <h3>还没有购买任何乐谱</h3>
                        <p>浏览我们的乐谱库，找到您喜欢的曲目</p>
                        <button class="btn btn-primary" onclick="UserPages.hide()">开始浏览</button>
                    </div>
                `;
                return;
            }

            // Group by composer
            const grouped = {};
            purchases.forEach(p => {
                if (!grouped[p.composer_slug]) {
                    grouped[p.composer_slug] = [];
                }
                grouped[p.composer_slug].push(p);
            });

            let html = '<div class="purchases-list">';
            Object.entries(grouped).forEach(([composer, scores]) => {
                html += `
                    <div class="purchase-group">
                        <h3 class="composer-name">${composer.charAt(0).toUpperCase() + composer.slice(1)}</h3>
                        <div class="scores-grid">
                `;
                scores.forEach(score => {
                    html += `
                        <div class="score-card purchased">
                            <div class="score-info">
                                <div class="score-name">${score.score_name}</div>
                                <div class="score-meta">购买于 ${new Date(score.purchase_date).toLocaleDateString('zh-CN')}</div>
                            </div>
                            <div class="score-actions">
                                <button class="btn btn-small btn-primary" onclick="UserPages.downloadScore(${score.id})">下载 PDF</button>
                                <button class="btn btn-small" onclick="UserPages.previewScore('${score.composer_slug}', '${score.score_path}')">预览</button>
                            </div>
                        </div>
                    `;
                });
                html += '</div></div>';
            });
            html += '</div>';

            content.innerHTML = html;
        } catch (err) {
            content.innerHTML = `<div class="error">加载失败: ${err.message}</div>`;
        }
    },

    // Favorites
    async showFavorites() {
        if (!API.isLoggedIn()) {
            AuthUI.show('login');
            return;
        }

        this.show('我的收藏');
        const content = document.getElementById('userPageContent');
        content.innerHTML = '<div class="loading">加载中...</div>';

        try {
            const { favorites } = await API.favorites.list();

            if (favorites.length === 0) {
                content.innerHTML = `
                    <div class="empty-state">
                        <div class="empty-icon">❤️</div>
                        <h3>收藏夹是空的</h3>
                        <p>点击乐谱上的心形图标添加到收藏</p>
                        <button class="btn btn-primary" onclick="UserPages.hide()">开始浏览</button>
                    </div>
                `;
                return;
            }

            // Load score details from index
            const indexData = window.composersData || {};

            let html = '<div class="favorites-grid">';
            favorites.forEach(fav => {
                const composer = indexData[fav.composer_slug];
                const score = composer?.scores?.find(s => s.pdf === fav.score_path);
                const scoreName = score?.name || fav.score_path.split('/').pop().replace('.pdf', '');

                html += `
                    <div class="score-card favorite-card" data-path="${fav.score_path}">
                        <button class="favorite-btn active" onclick="UserPages.removeFavorite('${fav.score_path}', this)">
                            ❤️
                        </button>
                        <div class="score-info" onclick="UserPages.previewScore('${fav.composer_slug}', '${fav.score_path}')">
                            <div class="score-name">${scoreName}</div>
                            <div class="score-composer">${fav.composer_slug}</div>
                        </div>
                        <div class="score-actions">
                            <span class="score-price">$2.99</span>
                            <button class="btn btn-small btn-cart" onclick="Cart.addItem('${fav.composer_slug}', '${fav.score_path}', '${scoreName.replace(/'/g, "\\'")}', 2.99)">加入购物车</button>
                        </div>
                    </div>
                `;
            });
            html += '</div>';

            content.innerHTML = html;
        } catch (err) {
            content.innerHTML = `<div class="error">加载失败: ${err.message}</div>`;
        }
    },

    // Order History
    async showOrders() {
        if (!API.isLoggedIn()) {
            AuthUI.show('login');
            return;
        }

        this.show('订单历史');
        const content = document.getElementById('userPageContent');
        content.innerHTML = '<div class="loading">加载中...</div>';

        try {
            const { orders } = await API.orders.list();

            if (orders.length === 0) {
                content.innerHTML = `
                    <div class="empty-state">
                        <div class="empty-icon">📋</div>
                        <h3>还没有订单记录</h3>
                        <p>购买乐谱后，订单会显示在这里</p>
                        <button class="btn btn-primary" onclick="UserPages.hide()">开始购物</button>
                    </div>
                `;
                return;
            }

            let html = '<div class="orders-list">';
            orders.forEach(order => {
                const statusMap = {
                    'pending': '待支付',
                    'paid': '已支付',
                    'fulfilled': '已完成',
                    'expired': '已过期'
                };
                const statusClass = order.status === 'fulfilled' ? 'success' : (order.status === 'expired' ? 'error' : 'pending');

                html += `
                    <div class="order-card">
                        <div class="order-header">
                            <span class="order-id">订单 #${order.id}</span>
                            <span class="order-date">${new Date(order.created_at).toLocaleDateString('zh-CN')}</span>
                            <span class="order-status ${statusClass}">${statusMap[order.status] || order.status}</span>
                        </div>
                        <div class="order-items">
                            ${order.items.map(item => `
                                <div class="order-item">
                                    <span class="item-name">${item.score_name}</span>
                                    <span class="item-price">$${(item.price / 100).toFixed(2)}</span>
                                </div>
                            `).join('')}
                        </div>
                        <div class="order-footer">
                            <span class="order-total">总计: $${(order.total_amount / 100).toFixed(2)}</span>
                        </div>
                    </div>
                `;
            });
            html += '</div>';

            content.innerHTML = html;
        } catch (err) {
            content.innerHTML = `<div class="error">加载失败: ${err.message}</div>`;
        }
    },

    // Download purchased score
    async downloadScore(purchaseId) {
        const url = API.download.getUrl(purchaseId);
        // Add token to URL for auth
        const link = document.createElement('a');
        link.href = url;
        link.download = '';

        // For authenticated download, we need to fetch with token
        try {
            const response = await fetch(url, {
                headers: { 'Authorization': `Bearer ${API.token}` }
            });

            if (!response.ok) {
                const err = await response.json();
                throw new Error(err.error);
            }

            const blob = await response.blob();
            const blobUrl = URL.createObjectURL(blob);
            link.href = blobUrl;
            link.download = 'score.pdf';
            link.click();
            URL.revokeObjectURL(blobUrl);
        } catch (err) {
            AuthUI.showToast('下载失败: ' + err.message, 'error');
        }
    },

    // Preview score (open PDF modal)
    previewScore(composerSlug, scorePath) {
        // Trigger the existing PDF modal
        if (typeof openPdfModal === 'function') {
            openPdfModal(composerSlug, scorePath);
        }
    },

    // Remove from favorites
    async removeFavorite(scorePath, btn) {
        try {
            await API.favorites.remove(scorePath);
            const card = btn.closest('.score-card');
            card.style.opacity = '0';
            setTimeout(() => card.remove(), 300);
            AuthUI.showToast('已从收藏夹移除', 'info');
        } catch (err) {
            AuthUI.showToast('操作失败: ' + err.message, 'error');
        }
    }
};

// Initialize when DOM ready
