const express = require('express');
const router = express.Router();
const path = require('path');
const fs = require('fs');

// Load index data
let indexData = null;
let composerMeta = null;

function loadData() {
    if (!indexData) {
        const indexPath = path.join(__dirname, '../../site/data/index.json');
        const metaPath = path.join(__dirname, '../../site/data/composer_meta.json');
        indexData = JSON.parse(fs.readFileSync(indexPath, 'utf8'));
        composerMeta = JSON.parse(fs.readFileSync(metaPath, 'utf8'));
    }
    return { indexData, composerMeta };
}

// Genre/Type keywords mapping
const GENRE_KEYWORDS = {
    'sonata': ['sonata', 'sonate', 'sonatina', 'sonatine'],
    'nocturne': ['nocturne', 'notturno'],
    'etude': ['etude', 'étude', 'study', 'studies', 'exercise'],
    'waltz': ['waltz', 'walzer', 'valse'],
    'mazurka': ['mazurka', 'mazur'],
    'polonaise': ['polonaise', 'polacca'],
    'prelude': ['prelude', 'prélude', 'preludio'],
    'fugue': ['fugue', 'fuge', 'fuga'],
    'ballade': ['ballade', 'ballad'],
    'scherzo': ['scherzo'],
    'impromptu': ['impromptu'],
    'fantasy': ['fantasy', 'fantaisie', 'fantasia', 'fantasie'],
    'rondo': ['rondo'],
    'variation': ['variation', 'variations', 'variationen'],
    'concerto': ['concerto', 'konzert'],
    'minuet': ['minuet', 'menuet', 'menuett'],
    'march': ['march', 'marsch', 'marche'],
    'gavotte': ['gavotte'],
    'invention': ['invention', 'inventio'],
    'suite': ['suite'],
    'bagatelle': ['bagatelle'],
    'rhapsody': ['rhapsody', 'rhapsodie'],
    'berceuse': ['berceuse', 'lullaby'],
    'barcarolle': ['barcarolle', 'barcarola']
};

// Difficulty estimation based on composer and keywords
function estimateDifficulty(scoreName, composerSlug) {
    const name = scoreName.toLowerCase();

    // Easy indicators (1-3)
    if (name.includes('easy') || name.includes('simple') || name.includes('beginner')) return 2;
    if (name.includes('sonatina') || name.includes('minuet') || name.includes('menuet')) return 3;
    if (name.includes('invention') && name.includes('2-part')) return 4;

    // Medium indicators (4-6)
    if (name.includes('waltz') || name.includes('walzer') || name.includes('mazurka')) return 5;
    if (name.includes('nocturne') && composerSlug === 'chopin') return 6;
    if (name.includes('prelude') && composerSlug === 'bach') return 5;

    // Hard indicators (7-9)
    if (name.includes('ballade') || name.includes('scherzo')) return 8;
    if (name.includes('etude') && (composerSlug === 'chopin' || composerSlug === 'liszt')) return 8;
    if (name.includes('sonata') && (composerSlug === 'beethoven' || composerSlug === 'liszt')) return 7;

    // Very hard (9-10)
    if (name.includes('transcendental') || name.includes('paganini')) return 10;
    if (composerSlug === 'liszt' && (name.includes('rhapsody') || name.includes('mephisto'))) return 9;

    // Default by composer
    const composerDifficulty = {
        'burgmueller': 3, 'czerny': 4, 'clementi': 4, 'kuhlau': 4,
        'bach': 5, 'mozart': 5, 'haydn': 5, 'schubert': 6,
        'beethoven': 6, 'chopin': 7, 'schumann': 6, 'brahms': 7,
        'liszt': 8, 'rachmaninoff': 8, 'prokofiev': 7, 'debussy': 6, 'ravel': 7
    };

    return composerDifficulty[composerSlug] || 5;
}

// Detect genre from score name
function detectGenre(scoreName) {
    const name = scoreName.toLowerCase();
    for (const [genre, keywords] of Object.entries(GENRE_KEYWORDS)) {
        if (keywords.some(kw => name.includes(kw))) {
            return genre;
        }
    }
    return null;
}

// Extract opus number
function extractOpus(scoreName) {
    const match = scoreName.match(/op\.?\s*(\d+)/i);
    return match ? parseInt(match[1]) : null;
}

// GET /api/search - Full text search
router.get('/', (req, res) => {
    try {
        const { q, limit = 50 } = req.query;

        if (!q || q.trim().length < 2) {
            return res.status(400).json({ error: 'Search query must be at least 2 characters' });
        }

        const { indexData, composerMeta } = loadData();
        const query = q.toLowerCase().trim();
        const results = [];

        for (const [slug, composer] of Object.entries(indexData)) {
            const meta = composerMeta[slug] || {};
            const composerMatch = slug.includes(query) || composer.name.toLowerCase().includes(query);

            for (const score of composer.scores) {
                const scoreMatch = score.name.toLowerCase().includes(query);

                if (composerMatch || scoreMatch) {
                    results.push({
                        composerSlug: slug,
                        composerName: composer.name,
                        scoreName: score.name,
                        scorePath: score.pdf,
                        hasMidi: !!score.midi,
                        period: meta.period || null,
                        years: meta.years || null,
                        genre: detectGenre(score.name),
                        difficulty: estimateDifficulty(score.name, slug),
                        opus: extractOpus(score.name),
                        relevance: scoreMatch ? 2 : 1 // Score name match is more relevant
                    });
                }
            }
        }

        // Sort by relevance, then by name
        results.sort((a, b) => b.relevance - a.relevance || a.scoreName.localeCompare(b.scoreName));

        res.json({
            query: q,
            total: results.length,
            results: results.slice(0, parseInt(limit))
        });
    } catch (err) {
        console.error('Search error:', err);
        res.status(500).json({ error: 'Search failed' });
    }
});

// GET /api/search/advanced - Advanced search with filters
router.get('/advanced', (req, res) => {
    try {
        const { 
            q = '', 
            composer,
            period, 
            genre, 
            minDifficulty, 
            maxDifficulty,
            hasMidi,
            sort = 'relevance',
            limit = 50,
            offset = 0
        } = req.query;

        const { indexData, composerMeta } = loadData();
        const query = q.toLowerCase().trim();
        let results = [];

        for (const [slug, composerData] of Object.entries(indexData)) {
            const meta = composerMeta[slug] || {};

            // Filter by composer
            if (composer && slug !== composer.toLowerCase()) continue;

            // Filter by period
            if (period && meta.period !== period) continue;

            for (const score of composerData.scores) {
                const scoreName = score.name.toLowerCase();
                const scoreGenre = detectGenre(score.name);
                const difficulty = estimateDifficulty(score.name, slug);

                // Filter by genre
                if (genre && scoreGenre !== genre) continue;

                // Filter by difficulty
                if (minDifficulty && difficulty < parseInt(minDifficulty)) continue;
                if (maxDifficulty && difficulty > parseInt(maxDifficulty)) continue;

                // Filter by MIDI availability
                if (hasMidi === 'true' && !score.midi) continue;

                // Text search (if query provided)
                let relevance = 1;
                if (query) {
                    const nameMatch = scoreName.includes(query);
                    const composerMatch = slug.includes(query) || composerData.name.toLowerCase().includes(query);
                    if (!nameMatch && !composerMatch) continue;
                    relevance = nameMatch ? 2 : 1;
                }

                results.push({
                    composerSlug: slug,
                    composerName: composerData.name,
                    scoreName: score.name,
                    scorePath: score.pdf,
                    hasMidi: !!score.midi,
                    period: meta.period || null,
                    years: meta.years || null,
                    country: meta.country || null,
                    genre: scoreGenre,
                    difficulty,
                    opus: extractOpus(score.name),
                    relevance
                });
            }
        }

        // Sort results
        switch (sort) {
            case 'difficulty_asc':
                results.sort((a, b) => a.difficulty - b.difficulty);
                break;
            case 'difficulty_desc':
                results.sort((a, b) => b.difficulty - a.difficulty);
                break;
            case 'composer':
                results.sort((a, b) => a.composerName.localeCompare(b.composerName));
                break;
            case 'name':
                results.sort((a, b) => a.scoreName.localeCompare(b.scoreName));
                break;
            default: // relevance
                results.sort((a, b) => b.relevance - a.relevance || a.scoreName.localeCompare(b.scoreName));
        }

        const total = results.length;
        results = results.slice(parseInt(offset), parseInt(offset) + parseInt(limit));

        res.json({
            total,
            offset: parseInt(offset),
            limit: parseInt(limit),
            results
        });
    } catch (err) {
        console.error('Advanced search error:', err);
        res.status(500).json({ error: 'Search failed' });
    }
});

// GET /api/search/genres - List all genres
router.get('/genres', (req, res) => {
    res.json({
        genres: Object.keys(GENRE_KEYWORDS).map(g => ({
            id: g,
            label: g.charAt(0).toUpperCase() + g.slice(1)
        }))
    });
});

// GET /api/search/suggestions - Autocomplete suggestions
router.get('/suggestions', (req, res) => {
    try {
        const { q, limit = 10 } = req.query;

        if (!q || q.trim().length < 1) {
            return res.json({ suggestions: [] });
        }

        const { indexData } = loadData();
        const query = q.toLowerCase().trim();
        const suggestions = new Set();

        // Add matching composers
        for (const [slug, composer] of Object.entries(indexData)) {
            if (suggestions.size >= limit) break;
            if (slug.includes(query) || composer.name.toLowerCase().includes(query)) {
                suggestions.add(composer.name);
            }
        }

        // Add matching score names
        for (const [slug, composer] of Object.entries(indexData)) {
            if (suggestions.size >= limit) break;
            for (const score of composer.scores) {
                if (suggestions.size >= limit) break;
                if (score.name.toLowerCase().includes(query)) {
                    suggestions.add(score.name);
                }
            }
        }

        res.json({ suggestions: Array.from(suggestions).slice(0, limit) });
    } catch (err) {
        console.error('Suggestions error:', err);
        res.status(500).json({ error: 'Failed to get suggestions' });
    }
});

module.exports = router;

const express = require('express');
const router = express.Router();
const path = require('path');
const fs = require('fs');
const { db } = require('../models/database');
const { optionalAuth } = require('../middleware/auth');

// Load index data
let indexData = null;
let composerMeta = null;

function loadData() {
    if (!indexData) {
        const indexPath = path.join(__dirname, '../../site/data/index.json');
        const metaPath = path.join(__dirname, '../../site/data/composer_meta.json');
        indexData = JSON.parse(fs.readFileSync(indexPath, 'utf8'));
        composerMeta = JSON.parse(fs.readFileSync(metaPath, 'utf8'));
    }
    return { indexData, composerMeta };
}

// GET /api/recommendations/similar/:composerSlug/:scorePath
// Get similar scores based on composer, period, and genre
router.get('/similar/:composerSlug/*', (req, res) => {
    try {
        const { composerSlug } = req.params;
        const scorePath = req.params[0]; // Everything after composerSlug/
        const { limit = 10 } = req.query;

        const { indexData, composerMeta } = loadData();
        const meta = composerMeta[composerSlug] || {};
        const composer = indexData[composerSlug];

        if (!composer) {
            return res.status(404).json({ error: 'Composer not found' });
        }

        const currentScore = composer.scores.find(s => s.pdf === scorePath || s.pdf.endsWith(scorePath));
        const recommendations = [];

        // 1. Other scores from same composer (highest priority)
        composer.scores.forEach(score => {
            if (score.pdf !== scorePath && score.pdf !== currentScore?.pdf) {
                recommendations.push({
                    composerSlug,
                    composerName: composer.name,
                    scoreName: score.name,
                    scorePath: score.pdf,
                    hasMidi: !!score.midi,
                    reason: 'same_composer',
                    score: 10
                });
            }
        });

        // 2. Scores from same period
        if (meta.period) {
            for (const [slug, comp] of Object.entries(indexData)) {
                if (slug === composerSlug) continue;
                const compMeta = composerMeta[slug] || {};
                if (compMeta.period === meta.period) {
                    // Add up to 3 scores from each composer
                    comp.scores.slice(0, 3).forEach(score => {
                        recommendations.push({
                            composerSlug: slug,
                            composerName: comp.name,
                            scoreName: score.name,
                            scorePath: score.pdf,
                            hasMidi: !!score.midi,
                            reason: 'same_period',
                            period: compMeta.period,
                            score: 5
                        });
                    });
                }
            }
        }

        // Shuffle and limit results, prioritize by score
        recommendations.sort((a, b) => b.score - a.score + (Math.random() - 0.5));

        // Remove duplicates and limit
        const seen = new Set();
        const unique = recommendations.filter(r => {
            if (seen.has(r.scorePath)) return false;
            seen.add(r.scorePath);
            return true;
        });

        res.json({
            basedOn: {
                composer: composer.name,
                scorePath,
                period: meta.period
            },
            recommendations: unique.slice(0, parseInt(limit))
        });
    } catch (err) {
        console.error('Recommendations error:', err);
        res.status(500).json({ error: 'Failed to get recommendations' });
    }
});

// GET /api/recommendations/for-you
// Personalized recommendations based on user's favorites and purchases
router.get('/for-you', optionalAuth, (req, res) => {
    try {
        const { limit = 20 } = req.query;
        const { indexData, composerMeta } = loadData();

        if (!req.user) {
            // For non-logged-in users, return popular/featured scores
            return res.json({
                type: 'featured',
                recommendations: getFeaturedScores(indexData, composerMeta, parseInt(limit))
            });
        }

        // Get user's favorites and purchases
        const favorites = db.prepare(`
            SELECT composer_slug, score_path FROM favorites WHERE user_id = ?
        `).all(req.user.id);

        const purchases = db.prepare(`
            SELECT composer_slug, score_path FROM purchases WHERE user_id = ?
        `).all(req.user.id);

        const userItems = [...favorites, ...purchases];

        if (userItems.length === 0) {
            return res.json({
                type: 'featured',
                recommendations: getFeaturedScores(indexData, composerMeta, parseInt(limit))
            });
        }

        // Analyze user preferences
        const preferredComposers = {};
        const preferredPeriods = {};
        const ownedScores = new Set();

        userItems.forEach(item => {
            ownedScores.add(item.score_path);
            preferredComposers[item.composer_slug] = (preferredComposers[item.composer_slug] || 0) + 1;
            const meta = composerMeta[item.composer_slug];
            if (meta?.period) {
                preferredPeriods[meta.period] = (preferredPeriods[meta.period] || 0) + 1;
            }
        });

        // Generate recommendations
        const recommendations = [];

        // 1. More from favorite composers
        for (const [slug, count] of Object.entries(preferredComposers)) {
            const composer = indexData[slug];
            if (!composer) continue;

            composer.scores.forEach(score => {
                if (!ownedScores.has(score.pdf)) {
                    recommendations.push({
                        composerSlug: slug,
                        composerName: composer.name,
                        scoreName: score.name,
                        scorePath: score.pdf,
                        hasMidi: !!score.midi,
                        reason: 'favorite_composer',
                        score: count * 3
                    });
                }
            });
        }

        // 2. From same periods
        for (const [period, count] of Object.entries(preferredPeriods)) {
            for (const [slug, composer] of Object.entries(indexData)) {
                const meta = composerMeta[slug];
                if (meta?.period !== period) continue;
                if (preferredComposers[slug]) continue; // Already added

                composer.scores.slice(0, 5).forEach(score => {
                    if (!ownedScores.has(score.pdf)) {
                        recommendations.push({
                            composerSlug: slug,
                            composerName: composer.name,
                            scoreName: score.name,
                            scorePath: score.pdf,
                            hasMidi: !!score.midi,
                            reason: 'similar_period',
                            period,
                            score: count * 2
                        });
                    }
                });
            }
        }

        // Sort by score and add some randomness
        recommendations.sort((a, b) => b.score - a.score + (Math.random() - 0.5) * 2);

        // Remove duplicates
        const seen = new Set();
        const unique = recommendations.filter(r => {
            if (seen.has(r.scorePath)) return false;
            seen.add(r.scorePath);
            return true;
        });

        res.json({
            type: 'personalized',
            preferences: {
                topComposers: Object.keys(preferredComposers).slice(0, 5),
                topPeriods: Object.keys(preferredPeriods).slice(0, 3)
            },
            recommendations: unique.slice(0, parseInt(limit))
        });
    } catch (err) {
        console.error('For-you recommendations error:', err);
        res.status(500).json({ error: 'Failed to get recommendations' });
    }
});

// GET /api/recommendations/popular
// Most favorited/purchased scores
router.get('/popular', (req, res) => {
    try {
        const { limit = 20, period } = req.query;
        const { indexData, composerMeta } = loadData();

        // Get popular scores from database
        const popular = db.prepare(`
            SELECT 
                composer_slug,
                score_path,
                COUNT(DISTINCT user_id) as favorite_count
            FROM favorites
            GROUP BY score_path
            ORDER BY favorite_count DESC
            LIMIT 100
        `).all();

        const recommendations = [];

        for (const item of popular) {
            const composer = indexData[item.composer_slug];
            if (!composer) continue;

            const meta = composerMeta[item.composer_slug] || {};
            if (period && meta.period !== period) continue;

            const score = composer.scores.find(s => s.pdf === item.score_path);
            if (!score) continue;

            recommendations.push({
                composerSlug: item.composer_slug,
                composerName: composer.name,
                scoreName: score.name,
                scorePath: score.pdf,
                hasMidi: !!score.midi,
                period: meta.period,
                favoriteCount: item.favorite_count
            });
        }

        // If not enough popular items, add featured
        if (recommendations.length < limit) {
            const featured = getFeaturedScores(indexData, composerMeta, limit - recommendations.length);
            const existingPaths = new Set(recommendations.map(r => r.scorePath));
            featured.forEach(f => {
                if (!existingPaths.has(f.scorePath)) {
                    recommendations.push(f);
                }
            });
        }

        res.json({
            recommendations: recommendations.slice(0, parseInt(limit))
        });
    } catch (err) {
        console.error('Popular recommendations error:', err);
        res.status(500).json({ error: 'Failed to get popular scores' });
    }
});

// Helper: Get featured scores from famous composers
function getFeaturedScores(indexData, composerMeta, limit) {
    const featured = [
        'bach', 'beethoven', 'chopin', 'mozart', 'debussy',
        'liszt', 'schumann', 'schubert', 'rachmaninoff', 'brahms'
    ];

    const recommendations = [];

    for (const slug of featured) {
        const composer = indexData[slug];
        if (!composer) continue;
        const meta = composerMeta[slug] || {};

        // Pick random scores from each composer
        const shuffled = [...composer.scores].sort(() => Math.random() - 0.5);
        shuffled.slice(0, 3).forEach(score => {
            recommendations.push({
                composerSlug: slug,
                composerName: composer.name,
                scoreName: score.name,
                scorePath: score.pdf,
                hasMidi: !!score.midi,
                period: meta.period,
                reason: 'featured'
            });
        });
    }

    return recommendations.sort(() => Math.random() - 0.5).slice(0, limit);
}

module.exports = router;

