// Advanced Search UI Component
const SearchUI = {
    panel: null,
    genres: [],
    searchTimeout: null,

    async init() {
        await this.loadGenres();
        this.createAdvancedPanel();
        this.enhanceSearchInput();
    },

    async loadGenres() {
        try {
            const data = await API.search.genres();
            this.genres = data.genres;
        } catch (err) {
            console.error('Failed to load genres:', err);
            this.genres = [];
        }
    },

    createAdvancedPanel() {
        const searchBox = document.querySelector('.search-box');
        if (!searchBox) return;

        // Create advanced search toggle
        const toggle = document.createElement('button');
        toggle.className = 'advanced-search-toggle';
        toggle.innerHTML = '⚙️';
        toggle.title = '高级搜索';
        toggle.addEventListener('click', () => this.togglePanel());
        searchBox.appendChild(toggle);

        // Create advanced panel
        const panel = document.createElement('div');
        panel.className = 'advanced-search-panel hidden';
        panel.innerHTML = `
            <div class="advanced-search-content">
                <h3>高级搜索</h3>

                <div class="search-filters">
                    <div class="filter-group">
                        <label>时期</label>
                        <select id="filterPeriod">
                            <option value="">全部时期</option>
                            <option value="baroque">巴洛克 (1600-1750)</option>
                            <option value="classical">古典 (1750-1820)</option>
                            <option value="romantic">浪漫 (1820-1900)</option>
                            <option value="modern">现代 (1900+)</option>
                        </select>
                    </div>

                    <div class="filter-group">
                        <label>类型</label>
                        <select id="filterGenre">
                            <option value="">全部类型</option>
                            ${this.genres.map(g => `<option value="${g.id}">${g.label}</option>`).join('')}
                        </select>
                    </div>

                    <div class="filter-group">
                        <label>难度范围</label>
                        <div class="difficulty-slider">
                            <input type="range" id="minDifficulty" min="1" max="10" value="1">
                            <span id="difficultyRange">1 - 10</span>
                            <input type="range" id="maxDifficulty" min="1" max="10" value="10">
                        </div>
                    </div>

                    <div class="filter-group">
                        <label class="checkbox-label">
                            <input type="checkbox" id="filterMidi">
                            仅显示有 MIDI 的乐谱
                        </label>
                    </div>
                </div>

                <div class="search-actions">
                    <button class="btn btn-secondary" id="resetFilters">重置</button>
                    <button class="btn btn-primary" id="applyFilters">搜索</button>
                </div>
            </div>
        `;

        searchBox.parentNode.insertBefore(panel, searchBox.nextSibling);
        this.panel = panel;

        // Bind events
        this.bindPanelEvents();
    },

    bindPanelEvents() {
        // Difficulty sliders
        const minSlider = document.getElementById('minDifficulty');
        const maxSlider = document.getElementById('maxDifficulty');
        const rangeDisplay = document.getElementById('difficultyRange');

        const updateRange = () => {
            let min = parseInt(minSlider.value);
            let max = parseInt(maxSlider.value);
            if (min > max) {
                [min, max] = [max, min];
                minSlider.value = min;
                maxSlider.value = max;
            }
            rangeDisplay.textContent = `${min} - ${max}`;
        };

        minSlider.addEventListener('input', updateRange);
        maxSlider.addEventListener('input', updateRange);

        // Reset button
        document.getElementById('resetFilters').addEventListener('click', () => {
            document.getElementById('filterPeriod').value = '';
            document.getElementById('filterGenre').value = '';
            minSlider.value = 1;
            maxSlider.value = 10;
            document.getElementById('filterMidi').checked = false;
            updateRange();
        });

        // Apply button
        document.getElementById('applyFilters').addEventListener('click', () => {
            this.executeAdvancedSearch();
        });

        // Close panel when clicking outside
        document.addEventListener('click', (e) => {
            if (!this.panel.contains(e.target) && 
                !e.target.classList.contains('advanced-search-toggle')) {
                this.panel.classList.add('hidden');
            }
        });
    },

    togglePanel() {
        this.panel.classList.toggle('hidden');
    },

    enhanceSearchInput() {
        const searchInput = document.getElementById('searchInput');
        if (!searchInput) return;

        // Create suggestions dropdown
        const suggestions = document.createElement('div');
        suggestions.className = 'search-suggestions hidden';
        searchInput.parentNode.appendChild(suggestions);

        // Add input listener for suggestions
        searchInput.addEventListener('input', (e) => {
            clearTimeout(this.searchTimeout);
            const query = e.target.value.trim();

            if (query.length < 2) {
                suggestions.classList.add('hidden');
                return;
            }

            this.searchTimeout = setTimeout(async () => {
                try {
                    const data = await API.search.suggestions(query);
                    if (data.suggestions.length > 0) {
                        suggestions.innerHTML = data.suggestions.map(s => 
                            `<div class="suggestion-item">${this.highlightMatch(s, query)}</div>`
                        ).join('');
                        suggestions.classList.remove('hidden');

                        // Click handler for suggestions
                        suggestions.querySelectorAll('.suggestion-item').forEach(item => {
                            item.addEventListener('click', () => {
                                searchInput.value = item.textContent;
                                suggestions.classList.add('hidden');
                                // Trigger search
                                searchInput.dispatchEvent(new Event('input', { bubbles: true }));
                            });
                        });
                    } else {
                        suggestions.classList.add('hidden');
                    }
                } catch (err) {
                    console.error('Suggestions error:', err);
                }
            }, 300);
        });

        // Hide suggestions on blur
        searchInput.addEventListener('blur', () => {
            setTimeout(() => suggestions.classList.add('hidden'), 200);
        });
    },

    highlightMatch(text, query) {
        const regex = new RegExp(`(${query})`, 'gi');
        return text.replace(regex, '<strong>$1</strong>');
    },

    async executeAdvancedSearch() {
        const params = {
            q: document.getElementById('searchInput').value.trim(),
            period: document.getElementById('filterPeriod').value,
            genre: document.getElementById('filterGenre').value,
            minDifficulty: document.getElementById('minDifficulty').value,
            maxDifficulty: document.getElementById('maxDifficulty').value,
            hasMidi: document.getElementById('filterMidi').checked ? 'true' : '',
            limit: 100
        };

        // Remove empty params
        Object.keys(params).forEach(key => {
            if (!params[key]) delete params[key];
        });

        try {
            this.panel.classList.add('hidden');
            const data = await API.search.advanced(params);
            this.displayResults(data.results, data.total);
        } catch (err) {
            console.error('Advanced search error:', err);
            AuthUI.showToast('搜索失败: ' + err.message, 'error');
        }
    },

    displayResults(results, total) {
        // Hide other sections
        document.getElementById('featuredSection').classList.add('hidden');
        document.getElementById('composerList').classList.add('hidden');
        document.getElementById('scoreList').classList.add('hidden');

        const searchResults = document.getElementById('searchResults');
        const content = document.getElementById('searchResultsContent');
        const searchTerm = document.getElementById('searchTerm');

        searchTerm.textContent = `找到 ${total} 个结果`;
        searchResults.classList.remove('hidden');

        if (results.length === 0) {
            content.innerHTML = `
                <div class="empty-state">
                    <div class="empty-icon">🔍</div>
                    <h3>没有找到匹配的乐谱</h3>
                    <p>尝试调整搜索条件</p>
                </div>
            `;
            return;
        }

        // Group by composer
        const grouped = {};
        results.forEach(r => {
            if (!grouped[r.composerSlug]) {
                grouped[r.composerSlug] = {
                    name: r.composerName,
                    period: r.period,
                    scores: []
                };
            }
            grouped[r.composerSlug].scores.push(r);
        });

        const periodColors = {
            'baroque': '#8b4513',
            'classical': '#2e7d32',
            'romantic': '#c62828',
            'modern': '#1565c0'
        };

        const difficultyLabels = {
            1: '入门', 2: '初级', 3: '初级', 4: '初中级', 5: '中级',
            6: '中高级', 7: '高级', 8: '高级', 9: '专业', 10: '大师'
        };

        let html = '';
        for (const [slug, data] of Object.entries(grouped)) {
            html += `
                <div class="search-group">
                    <h3 class="composer-name">
                        ${data.name}
                        ${data.period ? `<span class="period-badge" style="background: ${periodColors[data.period]}">${data.period}</span>` : ''}
                    </h3>
                    <div class="scores-grid">
            `;

            data.scores.forEach(score => {
                html += `
                    <div class="score-card search-result-card" 
                         data-pdf="${score.scorePath}" 
                         data-composer="${score.composerName}"
                         data-name="${score.scoreName}">
                        <div class="score-info">
                            <div class="score-name">${score.scoreName}</div>
                            <div class="score-tags">
                                ${score.genre ? `<span class="tag tag-genre">${score.genre}</span>` : ''}
                                <span class="tag tag-difficulty" title="难度 ${score.difficulty}/10">${difficultyLabels[score.difficulty]}</span>
                                ${score.hasMidi ? '<span class="tag tag-midi">MIDI</span>' : ''}
                            </div>
                        </div>
                        <div class="score-price">$2.99</div>
                    </div>
                `;
            });

            html += '</div></div>';
        }

        content.innerHTML = html;

        // Bind click events
        content.querySelectorAll('.score-card').forEach(card => {
            card.addEventListener('click', () => {
                if (typeof window.openPdfModal === 'function') {
                    window.openPdfModal(card.dataset.pdf, '', card.dataset.name, card.dataset.composer);
                }
            });
        });
    }
};

// Initialize when DOM ready
document.addEventListener('DOMContentLoaded', () => {
    // Wait for API to be ready
    setTimeout(() => SearchUI.init(), 500);
});

