// Shopping Cart Module with Stripe Integration
const Cart = {
    items: [],
    defaultPrice: 2.99,

    init() {
        // Load from localStorage
        const saved = localStorage.getItem('ezmusicstore_cart');
        if (saved) {
            this.items = JSON.parse(saved);
        }
        this.bindEvents();
        this.updateUI();
        this.checkUrlParams();
    },

    bindEvents() {
        // Cart button
        document.getElementById('cartBtn').addEventListener('click', () => this.toggleSidebar());
        document.getElementById('cartClose').addEventListener('click', () => this.closeSidebar());
        document.getElementById('cartOverlay').addEventListener('click', () => this.closeSidebar());

        // Checkout
        document.getElementById('checkoutBtn').addEventListener('click', () => this.openCheckout());
        document.getElementById('closeCheckout').addEventListener('click', () => this.closeCheckout());
        document.getElementById('checkoutForm').addEventListener('submit', (e) => this.handleCheckout(e));

        // Success modal
        document.getElementById('closeSuccess').addEventListener('click', () => {
            document.getElementById('successModal').classList.add('hidden');
            // Clear URL params
            window.history.replaceState({}, document.title, window.location.pathname);
        });
    },

    // Check URL for Stripe redirect results
    checkUrlParams() {
        const params = new URLSearchParams(window.location.search);

        if (params.get('success') === 'true') {
            const sessionId = params.get('session_id');
            if (sessionId) {
                this.handlePaymentSuccess(sessionId);
            }
        } else if (params.get('canceled') === 'true') {
            AuthUI.showToast('支付已取消', 'info');
            window.history.replaceState({}, document.title, window.location.pathname);
        }
    },

    async handlePaymentSuccess(sessionId) {
        try {
            // Verify payment with backend
            const data = await API.checkout.getSession(sessionId);

            if (data.status === 'paid' || data.status === 'complete') {
                // Clear cart
                this.clear();

                // Show success modal
                const links = document.getElementById('downloadLinks');
                if (data.order && data.order.items) {
                    links.innerHTML = data.order.items.map(item => `
                        <div class="download-item">
                            <span>📄 ${item.score_name}</span>
                        </div>
                    `).join('');
                    links.innerHTML += '<p class="download-note">下载链接已发送到您的邮箱，也可在「我的乐谱库」中查看</p>';
                }

                document.getElementById('successModal').classList.remove('hidden');
            }
        } catch (err) {
            console.error('Error verifying payment:', err);
            AuthUI.showToast('验证支付状态失败，请联系客服', 'error');
        }
    },

    addItem(composerSlug, scorePath, scoreName, price = 2.99) {
        // Check if already in cart
        const exists = this.items.find(i => i.scorePath === scorePath);
        if (exists) {
            this.showNotification('该乐谱已在购物车中');
            return;
        }

        this.items.push({
            composerSlug,
            scorePath,
            scoreName,
            composerName: composerSlug.charAt(0).toUpperCase() + composerSlug.slice(1),
            price: price || this.defaultPrice,
            addedAt: Date.now()
        });

        this.save();
        this.updateUI();
        this.showNotification('已添加到购物车');
        this.openSidebar();
    },

    // Legacy add method for backward compatibility
    add(item) {
        this.addItem(
            item.composer?.toLowerCase() || item.composerSlug || '',
            item.pdf || item.scorePath,
            item.name || item.scoreName,
            item.price
        );
    },

    remove(scorePath) {
        this.items = this.items.filter(i => i.scorePath !== scorePath);
        this.save();
        this.updateUI();
    },

    clear() {
        this.items = [];
        this.save();
        this.updateUI();
    },

    save() {
        localStorage.setItem('ezmusicstore_cart', JSON.stringify(this.items));
    },

    getTotal() {
        return this.items.reduce((sum, item) => sum + (item.price || this.defaultPrice), 0);
    },

    updateUI() {
        // Update cart count
        const count = this.items.length;
        const cartCount = document.getElementById('cartCount');
        if (cartCount) {
            cartCount.textContent = count;
            cartCount.style.display = count > 0 ? 'flex' : 'none';
        }

        // Update cart items
        const cartItems = document.getElementById('cartItems');
        if (!cartItems) return;

        if (count === 0) {
            cartItems.innerHTML = '<div class="cart-empty">购物车是空的</div>';
        } else {
            cartItems.innerHTML = this.items.map(item => `
                <div class="cart-item" data-path="${item.scorePath}">
                    <div class="cart-item-info">
                        <div class="cart-item-name">${item.scoreName}</div>
                        <div class="cart-item-composer">${item.composerName || ''}</div>
                    </div>
                    <div class="cart-item-price">$${(item.price || this.defaultPrice).toFixed(2)}</div>
                    <button class="cart-item-remove" data-path="${item.scorePath}">×</button>
                </div>
            `).join('');

            // Bind remove buttons
            cartItems.querySelectorAll('.cart-item-remove').forEach(btn => {
                btn.addEventListener('click', (e) => {
                    e.stopPropagation();
                    this.remove(btn.dataset.path);
                });
            });
        }

        // Update total
        const cartTotal = document.getElementById('cartTotal');
        if (cartTotal) {
            cartTotal.textContent = '$' + this.getTotal().toFixed(2);
        }

        // Update checkout button
        const checkoutBtn = document.getElementById('checkoutBtn');
        if (checkoutBtn) {
            checkoutBtn.disabled = count === 0;
        }
    },

    toggleSidebar() {
        const sidebar = document.getElementById('cartSidebar');
        const overlay = document.getElementById('cartOverlay');
        if (sidebar && overlay) {
            sidebar.classList.toggle('hidden');
            overlay.classList.toggle('hidden');
        }
    },

    openSidebar() {
        const sidebar = document.getElementById('cartSidebar');
        const overlay = document.getElementById('cartOverlay');
        if (sidebar && overlay) {
            sidebar.classList.remove('hidden');
            overlay.classList.remove('hidden');
        }
    },

    closeSidebar() {
        const sidebar = document.getElementById('cartSidebar');
        const overlay = document.getElementById('cartOverlay');
        if (sidebar && overlay) {
            sidebar.classList.add('hidden');
            overlay.classList.add('hidden');
        }
    },

    openCheckout() {
        this.closeSidebar();

        // Update checkout summary
        const summary = document.getElementById('checkoutSummary');
        summary.innerHTML = `
            <h4>订单详情 (${this.items.length} 项)</h4>
            <ul class="checkout-items">
                ${this.items.map(item => `
                    <li>
                        <span>${item.scoreName}</span>
                        <span>$${(item.price || this.defaultPrice).toFixed(2)}</span>
                    </li>
                `).join('')}
            </ul>
            <div class="checkout-total">
                <strong>总计:</strong>
                <strong>$${this.getTotal().toFixed(2)}</strong>
            </div>
        `;

        // Pre-fill email if logged in
        const emailInput = document.getElementById('customerEmail');
        if (emailInput && API.isLoggedIn() && API.getUser()) {
            emailInput.value = API.getUser().email;
        }

        document.getElementById('payAmount').textContent = '$' + this.getTotal().toFixed(2);
        document.getElementById('checkoutModal').classList.remove('hidden');
    },

    closeCheckout() {
        document.getElementById('checkoutModal').classList.add('hidden');
    },

    async handleCheckout(e) {
        e.preventDefault();

        const email = document.getElementById('customerEmail').value;
        const payBtn = document.getElementById('payBtn');

        payBtn.disabled = true;
        payBtn.textContent = '正在跳转到支付...';

        try {
            // Create Stripe checkout session via API
            const data = await API.checkout.createSession(this.items, email);

            if (data.url) {
                // Redirect to Stripe Checkout
                window.location.href = data.url;
            } else {
                throw new Error('No checkout URL returned');
            }
        } catch (err) {
            console.error('Checkout error:', err);
            AuthUI.showToast('创建支付会话失败: ' + err.message, 'error');

            payBtn.disabled = false;
            payBtn.innerHTML = '支付 <span id="payAmount">$' + this.getTotal().toFixed(2) + '</span>';
        }
    },

    showNotification(message) {
        if (typeof AuthUI !== 'undefined' && AuthUI.showToast) {
            AuthUI.showToast(message, 'success');
        } else {
            // Fallback notification
            const notif = document.createElement('div');
            notif.className = 'notification';
            notif.textContent = message;
            document.body.appendChild(notif);

            setTimeout(() => notif.classList.add('show'), 10);
            setTimeout(() => {
                notif.classList.remove('show');
                setTimeout(() => notif.remove(), 300);
            }, 2000);
        }
    }
};

