    // Get translation
    t(key, params = {}) {
        const lang = this.translations[this.currentLang] || this.translations['en'];
        let text = lang[key] || this.translations['en'][key] || key;

        // Replace parameters like {count}
        Object.keys(params).forEach(param => {
            text = text.replace(`{${param}}`, params[param]);
        });

        return text;
    },

    // Set language
    setLang(lang) {
        if (this.translations[lang]) {
            this.currentLang = lang;
            localStorage.setItem('lang', lang);
            this.updatePage();
            window.dispatchEvent(new CustomEvent('langChanged', { detail: { lang } }));
        }
    },

    // Get available languages
    getLanguages() {
        return [
            { code: 'en', name: 'English', flag: '🇺🇸' },
            { code: 'zh', name: '中文', flag: '🇨🇳' },
            { code: 'de', name: 'Deutsch', flag: '🇩🇪' },
            { code: 'ja', name: '日本語', flag: '🇯🇵' },
            { code: 'fr', name: 'Français', flag: '🇫🇷' }
        ];
    },

    // Update all elements with data-i18n attribute
    updatePage() {
        document.querySelectorAll('[data-i18n]').forEach(el => {
            const key = el.dataset.i18n;
            if (el.tagName === 'INPUT' && el.placeholder) {
                el.placeholder = this.t(key);
            } else {
                el.textContent = this.t(key);
            }
        });

        document.querySelectorAll('[data-i18n-placeholder]').forEach(el => {
            el.placeholder = this.t(el.dataset.i18nPlaceholder);
        });

        // Update HTML lang attribute
        document.documentElement.lang = this.currentLang;
    },

    // Create language selector
    createSelector() {
        const selector = document.createElement('div');
        selector.className = 'lang-selector';

        const current = this.getLanguages().find(l => l.code === this.currentLang);
        selector.innerHTML = `
            <button class="lang-btn">
                <span class="lang-flag">${current?.flag || '🌐'}</span>
                <span class="lang-code">${this.currentLang.toUpperCase()}</span>
            </button>
            <div class="lang-dropdown hidden">
                ${this.getLanguages().map(lang => `
                    <button class="lang-option ${lang.code === this.currentLang ? 'active' : ''}" data-lang="${lang.code}">
                        <span class="lang-flag">${lang.flag}</span>
                        <span class="lang-name">${lang.name}</span>
                    </button>
                `).join('')}
            </div>
        `;

        // Toggle dropdown
        const btn = selector.querySelector('.lang-btn');
        const dropdown = selector.querySelector('.lang-dropdown');

        btn.addEventListener('click', (e) => {
            e.stopPropagation();
            dropdown.classList.toggle('hidden');
        });

        // Select language
        selector.querySelectorAll('.lang-option').forEach(opt => {
            opt.addEventListener('click', () => {
                this.setLang(opt.dataset.lang);
                dropdown.classList.add('hidden');
                // Update button
                const lang = this.getLanguages().find(l => l.code === opt.dataset.lang);
                btn.querySelector('.lang-flag').textContent = lang.flag;
                btn.querySelector('.lang-code').textContent = lang.code.toUpperCase();
                // Update active state
                selector.querySelectorAll('.lang-option').forEach(o => o.classList.remove('active'));
                opt.classList.add('active');
            });
        });

        // Close on outside click
        document.addEventListener('click', () => dropdown.classList.add('hidden'));

        return selector;
    },

    init() {
        // Add language selector to header
        const headerRight = document.querySelector('.header-right');
        if (headerRight) {
            const selector = this.createSelector();
            headerRight.appendChild(selector);
        }

        // Initial page update
        this.updatePage();
    }
};

