// Utility Functions
const Utils = {

    /**
     * Simplify score name for display
     * Removes composer name prefix, redundant text, and formats nicely
     */
    simplifyScoreName(fullName, composerName = '') {
        let name = fullName;

        // Remove composer name from beginning (case insensitive)
        if (composerName) {
            const composerLower = composerName.toLowerCase();
            const nameLower = name.toLowerCase();
            if (nameLower.startsWith(composerLower)) {
                name = name.substring(composerName.length).trim();
                // Remove leading dash, underscore, or space
                name = name.replace(/^[-_\s]+/, '');
            }
        }

        // Remove common suffixes
        name = name.replace(/\s*-\s*Score$/i, '');
        name = name.replace(/\s*\(\d+\)$/, ''); // Remove (1), (2) etc
        name = name.replace(/\.pdf$/i, '');
        name = name.replace(/\.mid$/i, '');

        // Clean up multiple spaces
        name = name.replace(/\s+/g, ' ').trim();

        // If name is now empty, use original
        if (!name) {
            name = fullName.replace(/\.pdf$/i, '').replace(/\s*-\s*Score$/i, '');
        }

        return name;
    },

    /**
     * Format opus number nicely
     * "Op. 27 No.2" -> "Op.27 No.2"
     */
    formatOpus(name) {
        return name
            .replace(/Op\.?\s*(\d+)/gi, 'Op.$1')
            .replace(/No\.?\s*(\d+)/gi, 'No.$1')
            .replace(/BWV\.?\s*(\d+)/gi, 'BWV $1')
            .replace(/K\.?\s*(\d+)/gi, 'K.$1')
            .replace(/WoO\.?\s*(\d+)/gi, 'WoO $1');
    },

    /**
     * Truncate text with ellipsis
     */
    truncate(text, maxLength = 50) {
        if (text.length <= maxLength) return text;
        return text.substring(0, maxLength - 3).trim() + '...';
    },

    /**
     * Get display name for a score
     * Returns { short: '...', full: '...' }
     */
    getScoreDisplayName(fullName, composerName = '', maxShortLength = 45) {
        const simplified = this.simplifyScoreName(fullName, composerName);
        const formatted = this.formatOpus(simplified);

        return {
            short: this.truncate(formatted, maxShortLength),
            full: formatted,
            needsTruncation: formatted.length > maxShortLength
        };
    },

    /**
     * Parse score filename to extract metadata
     */
    parseScoreFilename(filename) {
        const result = {
            opus: null,
            number: null,
            key: null,
            type: null
        };

        // Extract opus
        const opusMatch = filename.match(/Op\.?\s*(\d+)/i);
        if (opusMatch) result.opus = parseInt(opusMatch[1]);

        // Extract number
        const noMatch = filename.match(/No\.?\s*(\d+)/i);
        if (noMatch) result.number = parseInt(noMatch[1]);

        // Extract key
        const keyMatch = filename.match(/in\s+([A-G][#b]?)\s*(major|minor|maj|min|dur|moll)?/i);
        if (keyMatch) {
            result.key = keyMatch[1] + (keyMatch[2] ? ' ' + keyMatch[2] : '');
        }

        // Common musical forms
        const types = [
            'Sonata', 'Sonatina', 'Nocturne', 'Etude', 'Waltz', 'Walzer', 'Valse',
            'Mazurka', 'Polonaise', 'Prelude', 'Fugue', 'Ballade', 'Scherzo',
            'Impromptu', 'Fantasy', 'Fantaisie', 'Rondo', 'Variation', 'Concerto',
            'Minuet', 'Menuet', 'March', 'Gavotte', 'Invention', 'Suite',
            'Bagatelle', 'Rhapsody', 'Berceuse', 'Barcarolle'
        ];

        for (const type of types) {
            if (filename.toLowerCase().includes(type.toLowerCase())) {
                result.type = type;
                break;
            }
        }

        return result;
    }
};

