// Utility Functions
const Utils = {

    /**
     * Decode URL-encoded characters in text
     * Handles cases like %C0 -> À, %E8 -> è
     */
    decodeText(text) {
        if (!text) return text;
        try {
            // First try standard decodeURIComponent
            return decodeURIComponent(text);
        } catch (e) {
            // If that fails (malformed), try manual replacement of common chars
            return text
                .replace(/%C0/gi, 'À').replace(/%C1/gi, 'Á').replace(/%C2/gi, 'Â')
                .replace(/%C3/gi, 'Ã').replace(/%C4/gi, 'Ä').replace(/%C5/gi, 'Å')
                .replace(/%C6/gi, 'Æ').replace(/%C7/gi, 'Ç').replace(/%C8/gi, 'È')
                .replace(/%C9/gi, 'É').replace(/%CA/gi, 'Ê').replace(/%CB/gi, 'Ë')
                .replace(/%CC/gi, 'Ì').replace(/%CD/gi, 'Í').replace(/%CE/gi, 'Î')
                .replace(/%CF/gi, 'Ï').replace(/%D0/gi, 'Ð').replace(/%D1/gi, 'Ñ')
                .replace(/%D2/gi, 'Ò').replace(/%D3/gi, 'Ó').replace(/%D4/gi, 'Ô')
                .replace(/%D5/gi, 'Õ').replace(/%D6/gi, 'Ö').replace(/%D8/gi, 'Ø')
                .replace(/%D9/gi, 'Ù').replace(/%DA/gi, 'Ú').replace(/%DB/gi, 'Û')
                .replace(/%DC/gi, 'Ü').replace(/%DD/gi, 'Ý').replace(/%DE/gi, 'Þ')
                .replace(/%DF/gi, 'ß').replace(/%E0/gi, 'à').replace(/%E1/gi, 'á')
                .replace(/%E2/gi, 'â').replace(/%E3/gi, 'ã').replace(/%E4/gi, 'ä')
                .replace(/%E5/gi, 'å').replace(/%E6/gi, 'æ').replace(/%E7/gi, 'ç')
                .replace(/%E8/gi, 'è').replace(/%E9/gi, 'é').replace(/%EA/gi, 'ê')
                .replace(/%EB/gi, 'ë').replace(/%EC/gi, 'ì').replace(/%ED/gi, 'í')
                .replace(/%EE/gi, 'î').replace(/%EF/gi, 'ï').replace(/%F0/gi, 'ð')
                .replace(/%F1/gi, 'ñ').replace(/%F2/gi, 'ò').replace(/%F3/gi, 'ó')
                .replace(/%F4/gi, 'ô').replace(/%F5/gi, 'õ').replace(/%F6/gi, 'ö')
                .replace(/%F8/gi, 'ø').replace(/%F9/gi, 'ù').replace(/%FA/gi, 'ú')
                .replace(/%FB/gi, 'û').replace(/%FC/gi, 'ü').replace(/%FD/gi, 'ý')
                .replace(/%FE/gi, 'þ').replace(/%FF/gi, 'ÿ').replace(/%20/gi, ' ');
        }
    },

    /**
     * Simplify score name for display
     * Removes composer name prefix, redundant text, and formats nicely
     */
    simplifyScoreName(rawName, composerName = '') {
        // Decode URL-encoded characters first
        let name = this.decodeText(rawName);

        // Remove composer name from beginning (case insensitive)
        if (composerName) {
            const composerLower = composerName.toLowerCase();
            const nameLower = name.toLowerCase();
            if (nameLower.startsWith(composerLower)) {
                name = name.substring(composerName.length).trim();
                // Remove leading dash, underscore, or space
                name = name.replace(/^[-_\s]+/, '');
            }
        }

        // Remove common suffixes
        name = name.replace(/\s*-\s*Score$/i, '');
        name = name.replace(/\s*\(\d+\)$/, ''); // Remove (1), (2) etc
        name = name.replace(/\.pdf$/i, '');
        name = name.replace(/\.mid$/i, '');

        // Clean up multiple spaces
        name = name.replace(/\s+/g, ' ').trim();

        // If name is now empty, use original (decoded)
        if (!name) {
            name = this.decodeText(rawName).replace(/\.pdf$/i, '').replace(/\s*-\s*Score$/i, '');
        }

        return name;
    },

    /**
     * Format opus number nicely
     * "Op. 27 No.2" -> "Op.27 No.2"
     */
    formatOpus(name) {
        return name
            .replace(/Op\.?\s*(\d+)/gi, 'Op.$1')
            .replace(/No\.?\s*(\d+)/gi, 'No.$1')
            .replace(/BWV\.?\s*(\d+)/gi, 'BWV $1')
            .replace(/K\.?\s*(\d+)/gi, 'K.$1')
            .replace(/WoO\.?\s*(\d+)/gi, 'WoO $1');
    },

    /**
     * Truncate text with ellipsis
     */
    truncate(text, maxLength = 50) {
        if (text.length <= maxLength) return text;
        return text.substring(0, maxLength - 3).trim() + '...';
    },

    /**
     * Get display name for a score
     * Returns { short: '...', full: '...' }
     */
    getScoreDisplayName(fullName, composerName = '', maxShortLength = 45) {
        const simplified = this.simplifyScoreName(fullName, composerName);
        const formatted = this.formatOpus(simplified);

        return {
            short: this.truncate(formatted, maxShortLength),
            full: formatted,
            needsTruncation: formatted.length > maxShortLength
        };
    },

    /**
     * Parse score filename to extract metadata
     */
    parseScoreFilename(filename) {
        const decoded = this.decodeText(filename);
        const result = {
            opus: null,
            number: null,
            key: null,
            type: null
        };

        // Extract opus
        const opusMatch = decoded.match(/Op\.?\s*(\d+)/i);
        if (opusMatch) result.opus = parseInt(opusMatch[1]);

        // Extract number
        const noMatch = decoded.match(/No\.?\s*(\d+)/i);
        if (noMatch) result.number = parseInt(noMatch[1]);

        // Extract key
        const keyMatch = decoded.match(/in\s+([A-G][#b]?)\s*(major|minor|maj|min|dur|moll)?/i);
        if (keyMatch) {
            result.key = keyMatch[1] + (keyMatch[2] ? ' ' + keyMatch[2] : '');
        }

        // Common musical forms
        const types = [
            'Sonata', 'Sonatina', 'Nocturne', 'Etude', 'Waltz', 'Walzer', 'Valse',
            'Mazurka', 'Polonaise', 'Prelude', 'Fugue', 'Ballade', 'Scherzo',
            'Impromptu', 'Fantasy', 'Fantaisie', 'Rondo', 'Variation', 'Concerto',
            'Minuet', 'Menuet', 'March', 'Gavotte', 'Invention', 'Suite',
            'Bagatelle', 'Rhapsody', 'Berceuse', 'Barcarolle'
        ];

        for (const type of types) {
            if (decoded.toLowerCase().includes(type.toLowerCase())) {
                result.type = type;
                break;
            }
        }

        return result;
    }
};

